const Merchandise = () => {
  const [orderComplete, setOrderComplete] = useState(false);
  const [orderDetails, setOrderDetails] = useState(null);
  const [cart, setCart] = useState([]);
  const [cartOpen, setCartOpen] = useState(false);
  const [selectedSizes, setSelectedSizes] = useState({});

  // Cart functions
  const addToCart = (item) => {
    if (item.category === 'Shirt' && !selectedSizes[item.id]) {
      alert('Please select a size for this t-shirt');
      return;
    }

    const cartItem = {
      ...item,
      size: selectedSizes[item.id] || 'N/A',
      quantity: 1
    };

    setCart(prevCart => {
      const existingItem = prevCart.find(cartItem => 
        cartItem.id === item.id && cartItem.size === (selectedSizes[item.id] || 'N/A')
      );

      if (existingItem) {
        return prevCart.map(cartItem => 
          cartItem.id === item.id && cartItem.size === (selectedSizes[item.id] || 'N/A')
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        );
      } else {
        return [...prevCart, cartItem];
      }
    });

    // Reset size selection for this item
    setSelectedSizes(prev => ({ ...prev, [item.id]: undefined }));
  };

  const removeFromCart = (itemId, size) => {
    setCart(prevCart => prevCart.filter(item => 
      !(item.id === itemId && item.size === size)
    ));
  };

  const updateQuantity = (itemId, size, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(itemId, size);
      return;
    }

    setCart(prevCart => prevCart.map(item => 
      item.id === itemId && item.size === size
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const getCartTotal = () => {
    return cart.reduce((total, item) => total + (item.priceUSD * item.quantity), 0);
  };

  const getCartItemCount = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const merchandiseItems = [
    {
      id: 1,
      title: "Danger Zone T-Shirt",
      category: "Shirt",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Official Smoking Snakes 'Danger Zone' T-Shirt featuring the album artwork. High-quality cotton with the band's signature design.",
      image: "/danger-zone-album-cover.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch",
      sizes: ["S", "M", "L", "XL", "XXL"]
    },
    {
      id: 2,
      title: "RAWK Guitar Pic Bundle",
      category: "Button/Pin/Patch",
      price: "€6 EUR",
      priceUSD: 6.50,
      description: "Smoking Snakes RAWK guitar pick bundle. Perfect for guitarists and collectors. Multiple picks with the band's logo.",
      image: "/band-photo-1.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 3,
      title: "Military Identification Necklace",
      category: "Other Apparel",
      price: "€20 EUR",
      priceUSD: 21.70,
      description: "Unique Smoking Snakes Military Identification Necklace. A distinctive piece of band merchandise with military-style design.",
      image: "/band-photo-2.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 4,
      title: "Classic Logo Snapback Trucker Cap",
      category: "Hat",
      price: "€25 EUR",
      priceUSD: 27.10,
      description: "Classic Smoking Snakes logo snapback trucker cap. Comfortable fit with embroidered band logo. Perfect for any occasion.",
      image: "/band-photo-3.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 5,
      title: "Classic Logo Patch – No Frills, Just Filth",
      category: "Other Apparel",
      price: "€8 EUR",
      priceUSD: 8.70,
      description: "🐍 Classic Smoking Snakes logo patch. No frills, just filth. Perfect for jackets, bags, or anywhere you want to show your support.",
      image: "/band-photo-4.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 6,
      title: "Scandinavian Premium Sleaze Metal – Limited Patch Drop!",
      category: "Other Apparel",
      price: "€9 EUR",
      priceUSD: 9.75,
      description: "🔥 Scandinavian Premium Sleaze Metal – Limited edition patch drop! Exclusive design celebrating the band's Swedish heritage and sleaze metal style.",
      image: "/danger-zone-album-cover.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 7,
      title: "Danger Zone - Red LP",
      category: "Vinyl",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Danger Zone - Red LP vinyl record. Limited edition red vinyl pressing of the debut album. A must-have for vinyl collectors and fans.",
      image: "/danger-zone-album-cover.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 8,
      title: "Classic Logo T-shirt",
      category: "Shirt",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Classic Smoking Snakes logo T-shirt. The iconic band logo on high-quality cotton. A timeless piece of band merchandise.",
      image: "/logo.png",
      available: false,
      link: "https://smokingsnakes.bandcamp.com/merch",
      sizes: ["S", "M", "L", "XL", "XXL"]
    }
  ];

  return (
    <MerchandiseContainer>
      <PageTitle
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        Merchandise
      </PageTitle>



      <MerchandiseGrid>
        {merchandiseItems.map((item, index) => (
          <MerchandiseCard
            key={item.id}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: index * 0.1 }}
          >
            <MerchandiseImage
              src={item.image}
              alt={item.title}
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'flex';
              }}
            />
            <div style={{ 
              display: 'none', 
              width: '100%', 
              height: '200px', 
              background: 'linear-gradient(135deg, #1a0000 0%, #2d0000 50%, #400000 100%)', 
              borderRadius: '12px', 
              alignItems: 'center', 
              justifyContent: 'center', 
              fontSize: '3rem', 
              color: '#ff0000' 
            }}>
              {item.category === 'Shirt' ? <FaTshirt /> : 
               item.category === 'Hat' ? <FaHatCowboy /> : 
               item.category === 'Vinyl' ? <FaMusic /> : 
               item.category === 'Button/Pin/Patch' ? <FaGuitar /> : 
               item.category === 'Other Apparel' ? <FaGem /> : <FaCircle />}
            </div>

            <MerchandiseTitle>
              {item.title}
            </MerchandiseTitle>

            <MerchandiseCategory>
              {item.category}
            </MerchandiseCategory>

            <MerchandisePrice>
              {item.price}
            </MerchandisePrice>

            <MerchandiseDescription>
              {item.description}
            </MerchandiseDescription>

            {item.available && (
              <>
                {item.category === 'Shirt' && (
                  <SizeSelector>
                    <SizeLabel>Select Size:</SizeLabel>
                    <SizeOptions>
                      {item.sizes.map(size => (
                        <SizeButton
                          key={size}
                          selected={selectedSizes[item.id] === size}
                          onClick={() => setSelectedSizes(prev => ({ ...prev, [item.id]: size }))}
                        >
                          {size}
                        </SizeButton>
                      ))}
                    </SizeOptions>
                  </SizeSelector>
                )}

                <AddToCartButton
                  onClick={() => addToCart(item)}
                  disabled={item.category === 'Shirt' && !selectedSizes[item.id]}
                >
                  <FaShoppingCart />
                  Add to Cart
                </AddToCartButton>

                <div style={{ textAlign: 'center', marginTop: '0.5rem' }}>
                  <small style={{ color: '#888888' }}>
                    Or <a href={item.link} target="_blank" rel="noopener noreferrer" style={{ color: '#ff0000', textDecoration: 'none' }}>buy on Bandcamp</a>
                  </small>
                </div>
              </>
            )}

            {!item.available && (
              <SoldOutBadge>SOLD OUT</SoldOutBadge>
            )}
          </MerchandiseCard>
        ))}
      </MerchandiseGrid>

      {/* Cart Icon */}
      <CartIcon onClick={() => setCartOpen(true)}>
        <FaShoppingCart />
        {getCartItemCount() > 0 && (
          <CartBadge>{getCartItemCount()}</CartBadge>
        )}
      </CartIcon>

      {/* Cart Modal */}
      {cartOpen && (
        <CartModal
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <CartContent>
            <CartHeader>
              <CartTitle>Shopping Cart ({getCartItemCount()} items)</CartTitle>
              <CloseButton onClick={() => setCartOpen(false)}>
                <FaTimes />
              </CloseButton>
            </CartHeader>

            {cart.length === 0 ? (
              <EmptyCart>
                <h3>Your cart is empty</h3>
                <p>Add some merchandise to get started!</p>
              </EmptyCart>
            ) : (
              <>
                {cart.map((item, index) => (
                  <CartItem key={`${item.id}-${item.size}-${index}`}>
                    <div style={{ position: 'relative' }}>
                      <CartItemImage
                        src={item.image}
                        alt={item.title}
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                      <CartItemImageFallback style={{ display: 'none' }}>
                        {item.category === 'Shirt' ? <FaTshirt /> : 
                         item.category === 'Hat' ? <FaHatCowboy /> : 
                         item.category === 'Vinyl' ? <FaMusic /> : 
                         item.category === 'Button/Pin/Patch' ? <FaGuitar /> : 
                         item.category === 'Other Apparel' ? <FaGem /> : <FaCircle />}
                      </CartItemImageFallback>
                    </div>
                    <CartItemDetails>
                      <CartItemTitle>{item.title}</CartItemTitle>
                      {item.category === 'Shirt' && (
                        <CartItemSize>Size: {item.size}</CartItemSize>
                      )}
                      <CartItemPrice>${item.priceUSD}</CartItemPrice>
                    </CartItemDetails>

                    <QuantityControl>
                      <QuantityButton
                        onClick={() => updateQuantity(item.id, item.size, item.quantity - 1)}
                      >
                        <FaMinus />
                      </QuantityButton>
                      <QuantityDisplay>{item.quantity}</QuantityDisplay>
                      <QuantityButton
                        onClick={() => updateQuantity(item.id, item.size, item.quantity + 1)}
                      >
                        <FaPlus />
                      </QuantityButton>
                    </QuantityControl>

                    <RemoveButton
                      onClick={() => removeFromCart(item.id, item.size)}
                    >
                      <FaTimes />
                    </RemoveButton>
                  </CartItem>
                ))}

                <CartTotal>
                  <TotalAmount>
                    Total: ${getCartTotal().toFixed(2)}
                  </TotalAmount>

                  <PayPalButtons
                    createOrder={(data, actions) => {
                      return actions.order.create({
                        purchase_units: [
                          {
                            amount: {
                              value: getCartTotal().toFixed(2),
                              currency_code: "USD"
                            },
                            description: `Smoking Snakes Merchandise - ${cart.length} items`,
                            custom_id: `cart-${Date.now()}`
                          }
                        ],
                        application_context: {
                          shipping_preference: "NO_SHIPPING"
                        }
                      });
                    }}
                    onApprove={(data, actions) => {
                      return actions.order.capture().then((details) => {
                        setOrderComplete(true);
                        setOrderDetails({
                          item: `Cart with ${cart.length} items`,
                          orderID: details.id,
                          payerID: details.payer.payer_id
                        });
                        setCart([]);
                        setCartOpen(false);
                      });
                    }}
                    onError={(err) => {
                      console.error("PayPal error:", err);
                    }}
                    style={{ layout: "horizontal" }}
                  />
                </CartTotal>
              </>
            )}
          </CartContent>
        </CartModal>
      )}

      {/* Order Success Message */}
      {orderComplete && orderDetails && (
        <OrderSuccess style={{ position: 'fixed', top: '20px', right: '20px', zIndex: 3000 }}>
          ✅ Order Complete! Order ID: {orderDetails.orderID}
        </OrderSuccess>
      )}
    </MerchandiseContainer>
  );
};

export default Merchandise;
