import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaTshirt, FaHatCowboy, FaGuitar, FaGem, FaFire, FaCircle, FaMusic, FaShoppingCart, FaPaypal } from 'react-icons/fa';
import { PayPalButtons } from '@paypal/react-paypal-js';

const MerchandiseContainer = styled.div`
  min-height: 100vh;
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const PageTitle = styled(motion.h1)`
  font-size: 3rem;
  text-align: center;
  margin-bottom: 3rem;
  color: #ff0000;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const HeroSection = styled.section`
  text-align: center;
  margin-bottom: 4rem;
  padding: 3rem 2rem;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const HeroTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const HeroDescription = styled.p`
  font-size: 1.2rem;
  color: #cccccc;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const MerchandiseGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
  margin-bottom: 4rem;
`;

const MerchandiseCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 16px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &:hover {
    transform: translateY(-5px);
    border-color: #ff0000;
    box-shadow: 0 10px 30px rgba(255, 0, 0, 0.2);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(45deg, #ff0000, #cc0000);
    transform: scaleX(0);
    transition: transform 0.3s ease;
  }

  &:hover::before {
    transform: scaleX(1);
  }
`;

const MerchandiseImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 12px;
  margin-bottom: 1.5rem;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const MerchandiseTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const MerchandiseCategory = styled.div`
  color: #ff6666;
  font-weight: 600;
  font-size: 0.9rem;
  margin-bottom: 1rem;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const MerchandisePrice = styled.div`
  font-size: 1.8rem;
  font-weight: 700;
  color: #64ffda;
  margin-bottom: 1.5rem;
`;

const MerchandiseDescription = styled.p`
  color: #cccccc;
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const BuyButton = styled.a`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  text-decoration: none;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.3);
  }
`;

const SoldOutBadge = styled.div`
  background: #666666;
  color: #ffffff;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  font-weight: 600;
  text-align: center;
  margin-top: 1rem;
`;

const Merchandise = () => {
  const [orderComplete, setOrderComplete] = useState(false);
  const [orderDetails, setOrderDetails] = useState(null);

  const merchandiseItems = [
    {
      id: 1,
      title: "Danger Zone T-Shirt",
      category: "Shirt",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Official Smoking Snakes 'Danger Zone' T-Shirt featuring the album artwork. High-quality cotton with the band's signature design.",
      image: "/merchandise/danger-zone-tshirt.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 2,
      title: "RAWK Guitar Pic Bundle",
      category: "Button/Pin/Patch",
      price: "€6 EUR",
      priceUSD: 6.50,
      description: "Smoking Snakes RAWK guitar pick bundle. Perfect for guitarists and collectors. Multiple picks with the band's logo.",
      image: "/merchandise/guitar-picks.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 3,
      title: "Military Identification Necklace",
      category: "Other Apparel",
      price: "€20 EUR",
      priceUSD: 21.70,
      description: "Unique Smoking Snakes Military Identification Necklace. A distinctive piece of band merchandise with military-style design.",
      image: "/merchandise/military-necklace.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 4,
      title: "Classic Logo Snapback Trucker Cap",
      category: "Hat",
      price: "€25 EUR",
      priceUSD: 27.10,
      description: "Classic Smoking Snakes logo snapback trucker cap. Comfortable fit with embroidered band logo. Perfect for any occasion.",
      image: "/merchandise/snapback-cap.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 5,
      title: "Classic Logo Patch – No Frills, Just Filth",
      category: "Other Apparel",
      price: "€8 EUR",
      priceUSD: 8.70,
      description: "🐍 Classic Smoking Snakes logo patch. No frills, just filth. Perfect for jackets, bags, or anywhere you want to show your support.",
      image: "/merchandise/logo-patch.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 6,
      title: "Scandinavian Premium Sleaze Metal – Limited Patch Drop!",
      category: "Other Apparel",
      price: "€9 EUR",
      priceUSD: 9.75,
      description: "🔥 Scandinavian Premium Sleaze Metal – Limited edition patch drop! Exclusive design celebrating the band's Swedish heritage and sleaze metal style.",
      image: "/merchandise/sleaze-patch.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 7,
      title: "Danger Zone - Red LP",
      category: "Vinyl",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Danger Zone - Red LP vinyl record. Limited edition red vinyl pressing of the debut album. A must-have for vinyl collectors and fans.",
      image: "/merchandise/red-lp.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 8,
      title: "Classic Logo T-shirt",
      category: "Shirt",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Classic Smoking Snakes logo T-shirt. The iconic band logo on high-quality cotton. A timeless piece of band merchandise.",
      image: "/merchandise/classic-logo-tshirt.jpg",
      available: false,
      link: "https://smokingsnakes.bandcamp.com/merch"
    }
  ];

  return (
    <MerchandiseContainer>
      <PageTitle
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        Merchandise
      </PageTitle>

      <HeroSection>
        <HeroTitle>Official Smoking Snakes Merchandise</HeroTitle>
        <HeroDescription>
          Show your support for the band with our exclusive merchandise collection. 
          From clothing to accessories, we've got everything a true Smoking Snakes fan needs.
        </HeroDescription>
      </HeroSection>

      <MerchandiseGrid>
        {merchandiseItems.map((item, index) => (
          <MerchandiseCard
            key={item.id}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: index * 0.1 }}
          >
            <MerchandiseImage
              src={item.image}
              alt={item.title}
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'flex';
              }}
            />
            <div style={{ 
              display: 'none', 
              width: '100%', 
              height: '200px', 
              background: 'linear-gradient(135deg, #1a0000 0%, #2d0000 50%, #400000 100%)', 
              borderRadius: '12px', 
              alignItems: 'center', 
              justifyContent: 'center', 
              fontSize: '3rem', 
              color: '#ff0000' 
            }}>
              {item.category === 'Shirt' ? <FaTshirt /> : 
               item.category === 'Hat' ? <FaHatCowboy /> : 
               item.category === 'Vinyl' ? <FaMusic /> : 
               item.category === 'Button/Pin/Patch' ? <FaGuitar /> : 
               item.category === 'Other Apparel' ? <FaGem /> : <FaCircle />}
            </div>

            <MerchandiseTitle>
              {item.title}
            </MerchandiseTitle>

            <MerchandiseCategory>
              {item.category}
            </MerchandiseCategory>

            <MerchandisePrice>
              {item.price}
            </MerchandisePrice>

            <MerchandiseDescription>
              {item.description}
            </MerchandiseDescription>

            {item.available ? (
              <BuyButton href={item.link} target="_blank" rel="noopener noreferrer">
                <FaShoppingCart />
                Buy Now
              </BuyButton>
            ) : (
              <SoldOutBadge>SOLD OUT</SoldOutBadge>
            )}
          </MerchandiseCard>
        ))}
      </MerchandiseGrid>
    </MerchandiseContainer>
  );
};

export default Merchandise;
