import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaPlay, FaSpotify, FaApple, FaYoutube, FaInstagram, FaTwitter } from 'react-icons/fa';

const HomeContainer = styled.div`
  min-height: 100vh;
  background: #000000;
  color: #ffffff;
  padding: 2rem;
`;

const HeroSection = styled.section`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  flex-direction: column;
`;

const HeroTitle = styled.h1`
  font-size: 3rem;
  margin-bottom: 1rem;
  color: #ff0000;
`;

const HeroSubtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 2rem;
  color: #cccccc;
`;

const CTAButton = styled.button`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  border: none;
  padding: 1rem 2rem;
  font-size: 1.2rem;
  font-weight: 600;
  border-radius: 50px;
  cursor: pointer;
  margin: 1rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(255, 0, 0, 0.3);
  }
`;

const SocialLinks = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-top: 2rem;
`;

const SocialIcon = styled.a`
  color: #ffffff;
  font-size: 1.5rem;
  padding: 0.5rem;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;

  &:hover {
    color: #ff0000;
    background: rgba(255, 0, 0, 0.2);
    transform: translateY(-2px);
  }
`;

const Home = () => {
  return (
    <HomeContainer>
      <HeroSection>
        <HeroTitle>Smoking Snakes</HeroTitle>
        <HeroSubtitle>Rock music that moves your soul</HeroSubtitle>
        <CTAButton as={Link} to="/music">
          <FaPlay style={{ marginRight: '0.5rem' }} />
          Listen Now
        </CTAButton>
        <SocialLinks>
          <SocialIcon href="#" target="_blank">
            <FaSpotify />
          </SocialIcon>
          <SocialIcon href="#" target="_blank">
            <FaApple />
          </SocialIcon>
          <SocialIcon href="#" target="_blank">
            <FaYoutube />
          </SocialIcon>
          <SocialIcon href="#" target="_blank">
            <FaInstagram />
          </SocialIcon>
          <SocialIcon href="#" target="_blank">
            <FaTwitter />
          </SocialIcon>
        </SocialLinks>
      </HeroSection>
    </HomeContainer>
  );
};

export default Home;
