import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaYoutube, FaPlay, FaCalendar, FaEye, FaHeart } from 'react-icons/fa';

const VideosContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const PageTitle = styled.h1`
  font-size: 3rem;
  color: #ff0000;
  text-align: center;
  margin-bottom: 1rem;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const PageSubtitle = styled.p`
  text-align: center;
  color: #cccccc;
  font-size: 1.2rem;
  margin-bottom: 3rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const HeroSection = styled.div`
  text-align: center;
  margin-bottom: 4rem;
  padding: 3rem 0;
  background: linear-gradient(135deg, rgba(255, 0, 0, 0.1) 0%, rgba(0, 0, 0, 0.3) 100%);
  border-radius: 15px;
  border: 1px solid rgba(255, 0, 0, 0.2);
`;

const HeroTitle = styled.h2`
  font-size: 2.5rem;
  color: #ffffff;
  margin-bottom: 1rem;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.7);
`;

const HeroDescription = styled.p`
  color: #cccccc;
  font-size: 1.1rem;
  max-width: 500px;
  margin: 0 auto;
  line-height: 1.6;
`;

const VideosGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
  margin-bottom: 3rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 1.5rem;
  }
`;

const VideoCard = styled(motion.div)`
  background: rgba(0, 0, 0, 0.6);
  border-radius: 15px;
  overflow: hidden;
  border: 1px solid rgba(255, 0, 0, 0.3);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    border-color: #ff0000;
    box-shadow: 0 10px 25px rgba(255, 0, 0, 0.3);
  }
`;

const VideoThumbnail = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  background: linear-gradient(135deg, #1a0000 0%, #2d0000 100%);
  border-radius: 10px;
  overflow: hidden;
`;

const YouTubeEmbed = styled.iframe`
  width: 100%;
  height: 100%;
  border: none;
  border-radius: 10px;
`;

const VideoInfo = styled.div`
  padding: 1.5rem;
`;

const VideoTitle = styled.h3`
  color: #ffffff;
  font-size: 1.3rem;
  margin-bottom: 1rem;
  line-height: 1.4;
`;

const VideoDescription = styled.p`
  color: #cccccc;
  font-size: 0.95rem;
  line-height: 1.5;
  margin-bottom: 1rem;
`;

const VideoMeta = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: #888888;
  font-size: 0.9rem;
`;

const VideoStats = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
`;

const StatItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.3rem;
`;

const WatchButton = styled.a`
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  background: #ff0000;
  color: white;
  padding: 0.8rem 1.5rem;
  border-radius: 25px;
  text-decoration: none;
  font-weight: 600;
  transition: all 0.3s ease;
  margin-top: 1rem;

  &:hover {
    background: #cc0000;
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.4);
  }
`;

const ChannelSection = styled.div`
  text-align: center;
  margin-top: 4rem;
  padding: 3rem;
  background: linear-gradient(135deg, rgba(255, 0, 0, 0.1) 0%, rgba(0, 0, 0, 0.3) 100%);
  border-radius: 15px;
  border: 1px solid rgba(255, 0, 0, 0.2);
`;

const ChannelButton = styled.a`
  display: inline-flex;
  align-items: center;
  gap: 0.8rem;
  background: #ff0000;
  color: white;
  padding: 1rem 2rem;
  border-radius: 30px;
  text-decoration: none;
  font-weight: 600;
  font-size: 1.2rem;
  transition: all 0.3s ease;

  &:hover {
    background: #cc0000;
    transform: translateY(-3px);
    box-shadow: 0 8px 20px rgba(255, 0, 0, 0.4);
  }
`;

const Videos = () => {
  const [videos] = useState([
    {
      id: 1,
      title: "Danger Zone - Official Music Video",
      description: "The official music video for 'Danger Zone', the title track from our debut album. Experience the raw energy and power of Scandinavian Premium Sleaze Metal!",
      videoId: "dQw4w9WgXcQ", // Replace with actual video ID
      views: "15.2K",
      likes: "1.2K",
      date: "2024-01-15"
    },
    {
      id: 2,
      title: "Live at Mayhem Rock Festival 2024",
      description: "Full live performance from Mayhem Rock Festival 2024. Watch us tear up the stage with our high-energy set!",
      videoId: "dQw4w9WgXcQ", // Replace with actual video ID
      views: "8.7K",
      likes: "856",
      date: "2024-05-17"
    },
    {
      id: 3,
      title: "Behind the Scenes: Recording 'Danger Zone'",
      description: "Exclusive behind-the-scenes footage from the recording sessions of our debut album 'Danger Zone'. See how the magic happens!",
      videoId: "dQw4w9WgXcQ", // Replace with actual video ID
      views: "6.3K",
      likes: "543",
      date: "2024-01-20"
    },
    {
      id: 4,
      title: "Guitar Solo Masterclass",
      description: "Learn the secrets behind our signature guitar solos. Perfect for aspiring metal guitarists!",
      videoId: "dQw4w9WgXcQ", // Replace with actual video ID
      views: "12.1K",
      likes: "987",
      date: "2024-02-10"
    },
    {
      id: 5,
      title: "Acoustic Session: 'Midnight Rider'",
      description: "An intimate acoustic performance of 'Midnight Rider' from our debut album. Stripped down and raw!",
      videoId: "dQw4w9WgXcQ", // Replace with actual video ID
      views: "9.8K",
      likes: "723",
      date: "2024-03-05"
    },
    {
      id: 6,
      title: "Tour Vlog: Road to Mayhem Festival",
      description: "Follow us on the road as we travel across Europe to perform at Mayhem Rock Festival 2024!",
      videoId: "dQw4w9WgXcQ", // Replace with actual video ID
      views: "7.4K",
      likes: "654",
      date: "2024-05-10"
    }
  ]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });
  };

  return (
    <VideosContainer>
      <PageTitle>Videos</PageTitle>
      <PageSubtitle>
        Watch our latest music videos, live performances, behind-the-scenes content, and exclusive footage
      </PageSubtitle>

      <HeroSection>
        <HeroTitle>Scandinavian Premium Sleaze Metal</HeroTitle>
        <HeroDescription>
          From electrifying live performances to intimate studio sessions, 
          discover the raw power and energy that defines Smoking Snakes
        </HeroDescription>
      </HeroSection>

      <VideosGrid>
        {videos.map((video) => (
          <VideoCard
            key={video.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            whileHover={{ scale: 1.02 }}
          >
            <VideoThumbnail>
              <PlayButton className="play-button">
                <FaPlay />
              </PlayButton>
              <img 
                src={video.thumbnail} 
                alt={video.title}
                style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                onError={(e) => {
                  e.target.style.display = 'none';
                }}
              />
            </VideoThumbnail>

            <VideoInfo>
              <VideoTitle>{video.title}</VideoTitle>
              <VideoDescription>{video.description}</VideoDescription>

              <VideoMeta>
                <VideoStats>
                  <StatItem>
                    <FaEye />
                    {video.views}
                  </StatItem>
                  <StatItem>
                    <FaHeart />
                    {video.likes}
                  </StatItem>
                  <StatItem>
                    <FaCalendar />
                    {formatDate(video.date)}
                  </StatItem>
                </VideoStats>
              </VideoMeta>

              <WatchButton href={video.url} target="_blank" rel="noopener noreferrer">
                <FaYoutube />
                Watch on YouTube
              </WatchButton>
            </VideoInfo>
          </VideoCard>
        ))}
      </VideosGrid>

      <ChannelSection>
        <h3 style={{ fontSize: '2rem', marginBottom: '1rem', color: '#ffffff' }}>
          Subscribe to Our Channel
        </h3>
        <p style={{ color: '#cccccc', marginBottom: '2rem', fontSize: '1.1rem' }}>
          Never miss a new video! Subscribe to our YouTube channel for exclusive content, 
          live performances, and behind-the-scenes footage.
        </p>
        <ChannelButton 
          href="https://www.youtube.com/@smokingsnakesofsweden" 
          target="_blank" 
          rel="noopener noreferrer"
        >
          <FaYoutube />
          Subscribe to Smoking Snakes
        </ChannelButton>
      </ChannelSection>
    </VideosContainer>
  );
};

export default Videos;
