import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { PayPalScriptProvider } from '@paypal/react-paypal-js';

// Components
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Music from './pages/Music';
import Tour from './pages/Tour';
import About from './pages/About';
import Contact from './pages/Contact';
import Merchandise from './pages/Merchandise';
import Videos from './pages/Videos';

// Hooks
import useScrollToTop from './hooks/useScrollToTop';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #000000 0%, #1a0000 50%, #2d0000 100%);
  color: #ffffff;
  font-family: 'Inter', sans-serif;
`;

const MainContent = styled(motion.main)`
  min-height: calc(100vh - 140px);
`;

function App() {
  // Enable automatic scrolling to top on route changes
  useScrollToTop();

  return (
    <PayPalScriptProvider options={{ 
      "client-id": "ATo17mx4_hrbo8qVCuFAQEfvcphyD9AzfzM6p7kqxP63LAZ1HO-ORU3Dy8Uz7X58MHQq4OVinpiKju6r", // Replace with your actual PayPal client ID
      currency: "USD"
    }}>
      <Router>
        <AppContainer>
          <Header />
          <MainContent
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/music" element={<Music />} />
              <Route path="/videos" element={<Videos />} />
              <Route path="/merchandise" element={<Merchandise />} />
              <Route path="/tour" element={<Tour />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
            </Routes>
          </MainContent>
          <Footer />
        </AppContainer>
      </Router>
    </PayPalScriptProvider>
  );
}

export default App;
