    <MerchandiseContainer>
      <PageTitle
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        Merchandise
      </PageTitle>



      <MerchandiseGrid>
        {merchandiseItems.map((item, index) => (
          <MerchandiseCard
            key={item.id}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: index * 0.1 }}
          >
            <MerchandiseImage
              src={item.image}
              alt={item.title}
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'flex';
              }}
            />
            <div style={{ 
              display: 'none', 
              width: '100%', 
              height: '200px', 
              background: 'linear-gradient(135deg, #1a0000 0%, #2d0000 50%, #400000 100%)', 
              borderRadius: '12px', 
              alignItems: 'center', 
              justifyContent: 'center', 
              fontSize: '3rem', 
              color: '#ff0000' 
            }}>
              {item.category === 'Shirt' ? <FaTshirt /> : 
               item.category === 'Hat' ? <FaHatCowboy /> : 
               item.category === 'Vinyl' ? <FaMusic /> : 
               item.category === 'Button/Pin/Patch' ? <FaGuitar /> : 
               item.category === 'Other Apparel' ? <FaGem /> : <FaCircle />}
            </div>

            <MerchandiseTitle>
              {item.title}
            </MerchandiseTitle>

            <MerchandiseCategory>
              {item.category}
            </MerchandiseCategory>

            <MerchandisePrice>
              {item.price}
            </MerchandisePrice>

            <MerchandiseDescription>
              {item.description}
            </MerchandiseDescription>

            {item.available && (
              <>
                {item.category === 'Shirt' && (
                  <SizeSelector>
                    <SizeLabel>Select Size:</SizeLabel>
                    <SizeOptions>
                      {item.sizes.map(size => (
                        <SizeButton
                          key={size}
                          selected={selectedSizes[item.id] === size}
                          onClick={() => setSelectedSizes(prev => ({ ...prev, [item.id]: size }))}
                        >
                          {size}
                        </SizeButton>
                      ))}
                    </SizeOptions>
                  </SizeSelector>
                )}

                <AddToCartButton
                  onClick={() => addToCart(item)}
                  disabled={item.category === 'Shirt' && !selectedSizes[item.id]}
                >
                  <FaShoppingCart />
                  Add to Cart
                </AddToCartButton>

                <div style={{ textAlign: 'center', marginTop: '0.5rem' }}>
                  <small style={{ color: '#888888' }}>
                    Or <a href={item.link} target="_blank" rel="noopener noreferrer" style={{ color: '#ff0000', textDecoration: 'none' }}>buy on Bandcamp</a>
                  </small>
                </div>
              </>
            )}

            {!item.available && (
              <SoldOutBadge>SOLD OUT</SoldOutBadge>
            )}
          </MerchandiseCard>
        ))}
      </MerchandiseGrid>

      {/* Cart Icon */}
      <CartIcon onClick={() => setCartOpen(true)}>
        <FaShoppingCart />
        {getCartItemCount() > 0 && (
          <CartBadge>{getCartItemCount()}</CartBadge>
        )}
      </CartIcon>

      {/* Cart Modal */}
      {cartOpen && (
        <CartModal
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <CartContent>
            <CartHeader>
              <CartTitle>Shopping Cart ({getCartItemCount()} items)</CartTitle>
              <CloseButton onClick={() => setCartOpen(false)}>
                <FaTimes />
              </CloseButton>
            </CartHeader>

            {cart.length === 0 ? (
              <EmptyCart>
                <h3>Your cart is empty</h3>
                <p>Add some merchandise to get started!</p>
              </EmptyCart>
            ) : (
              <>
                {cart.map((item, index) => (
                  <CartItem key={`${item.id}-${item.size}-${index}`}>
                    <div style={{ position: 'relative' }}>
                      <CartItemImage
                        src={item.image}
                        alt={item.title}
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                      <CartItemImageFallback style={{ display: 'none' }}>
                        {item.category === 'Shirt' ? <FaTshirt /> : 
                         item.category === 'Hat' ? <FaHatCowboy /> : 
                         item.category === 'Vinyl' ? <FaMusic /> : 
                         item.category === 'Button/Pin/Patch' ? <FaGuitar /> : 
                         item.category === 'Other Apparel' ? <FaGem /> : <FaCircle />}
                      </CartItemImageFallback>
                    </div>
                    <CartItemDetails>
                      <CartItemTitle>{item.title}</CartItemTitle>
                      {item.category === 'Shirt' && (
                        <CartItemSize>Size: {item.size}</CartItemSize>
                      )}
                      <CartItemPrice>${item.priceUSD}</CartItemPrice>
                    </CartItemDetails>

                    <QuantityControl>
                      <QuantityButton
                        onClick={() => updateQuantity(item.id, item.size, item.quantity - 1)}
                      >
                        <FaMinus />
                      </QuantityButton>
                      <QuantityDisplay>{item.quantity}</QuantityDisplay>
                      <QuantityButton
                        onClick={() => updateQuantity(item.id, item.size, item.quantity + 1)}
                      >
                        <FaPlus />
                      </QuantityButton>
                    </QuantityControl>

                    <RemoveButton
                      onClick={() => removeFromCart(item.id, item.size)}
                    >
                      <FaTimes />
                    </RemoveButton>
                  </CartItem>
                ))}

                <CartTotal>
                  <TotalAmount>
                    Total: ${getCartTotal().toFixed(2)}
                  </TotalAmount>

                  {/* Shipping Calculator */}
                  <ShippingSection>
                    <ShippingTitle>
                      <FaShippingFast />
                      Shipping & Delivery
                    </ShippingTitle>

                    {!POSTNORD_ENABLED && (
                      <div style={{ 
                        background: 'rgba(255, 165, 0, 0.1)', 
                        border: '1px solid rgba(255, 165, 0, 0.3)', 
                        borderRadius: '8px', 
                        padding: '1rem', 
                        marginBottom: '1rem',
                        color: '#ffa500'
                      }}>
                        <strong>💡 Pro Tip:</strong> To get real-time shipping rates from PostNord, add your API key to the .env file. 
                        <br />
                        <small>Currently using fallback rates. Get your API key from <a href="https://developer.postnord.com/" target="_blank" rel="noopener noreferrer" style={{ color: '#ffa500', textDecoration: 'underline' }}>developer.postnord.com</a></small>
                      </div>
                    )}



                    <ShippingForm>
                      <FormField>
                        <FormLabel>Country</FormLabel>
                        <FormSelect
                          value={shippingAddress.country}
                          onChange={(e) => setShippingAddress(prev => ({ ...prev, country: e.target.value }))}
                        >
                          {/* Nordic Countries */}
                          <optgroup label="Nordic Countries">
                            <option value="sweden">Sweden</option>
                            <option value="norway">Norway</option>
                            <option value="denmark">Denmark</option>
                            <option value="finland">Finland</option>
                            <option value="iceland">Iceland</option>
                          </optgroup>

                          {/* Europe */}
                          <optgroup label="Europe">
                            <option value="germany">Germany</option>
                            <option value="france">France</option>
                            <option value="uk">United Kingdom</option>
                            <option value="italy">Italy</option>
                            <option value="spain">Spain</option>
                            <option value="netherlands">Netherlands</option>
                            <option value="belgium">Belgium</option>
                            <option value="austria">Austria</option>
                            <option value="switzerland">Switzerland</option>
                            <option value="poland">Poland</option>
                            <option value="portugal">Portugal</option>
                            <option value="greece">Greece</option>
                            <option value="ireland">Ireland</option>
                            <option value="czechrepublic">Czech Republic</option>
                            <option value="hungary">Hungary</option>
                            <option value="romania">Romania</option>
                            <option value="bulgaria">Bulgaria</option>
                          </optgroup>

                          {/* North America */}
                          <optgroup label="North America">
                            <option value="usa">United States</option>
                            <option value="canada">Canada</option>
                            <option value="mexico">Mexico</option>
                          </optgroup>

                          {/* Asia Pacific */}
                          <optgroup label="Asia Pacific">
                            <option value="japan">Japan</option>
                            <option value="australia">Australia</option>
                            <option value="newzealand">New Zealand</option>
                            <option value="singapore">Singapore</option>
                            <option value="southkorea">South Korea</option>
                          </optgroup>

                          {/* South America */}
                          <optgroup label="South America">
                            <option value="brazil">Brazil</option>
                            <option value="argentina">Argentina</option>
                            <option value="chile">Chile</option>
                          </optgroup>

                          {/* Middle East */}
                          <optgroup label="Middle East">
                            <option value="israel">Israel</option>
                            <option value="uae">United Arab Emirates</option>
                            <option value="turkey">Turkey</option>
                          </optgroup>

                          {/* Africa */}
                          <optgroup label="Africa">
                            <option value="southafrica">South Africa</option>
                            <option value="egypt">Egypt</option>
                            <option value="nigeria">Nigeria</option>
                          </optgroup>

                          <option value="other">Other Countries</option>
                        </FormSelect>
                      </FormField>

                      <FormField>
                        <FormLabel>City</FormLabel>
                        <FormInput
                          type="text"
                          placeholder="Enter city"
                          value={shippingAddress.city}
                          onChange={(e) => setShippingAddress(prev => ({ ...prev, city: e.target.value }))}
                        />
                      </FormField>

                      <FormField>
                        <FormLabel>Postal Code</FormLabel>
                        <FormInput
                          type="text"
                          placeholder="Enter postal code"
                          value={shippingAddress.postalCode}
                          onChange={(e) => setShippingAddress(prev => ({ ...prev, postalCode: e.target.value }))}
                        />
                      </FormField>

                      <FormField>
                        <FormLabel>Address</FormLabel>
                        <FormInput
                          type="text"
                          placeholder="Enter full address"
                          value={shippingAddress.address}
                          onChange={(e) => setShippingAddress(prev => ({ ...prev, address: e.target.value }))}
                        />
                      </FormField>
                    </ShippingForm>

                    {isAddressComplete() && (
                      <ShippingMethods>
                        {Object.entries(getShippingRates(shippingAddress.country)).map(([method, details]) => (
                          <ShippingMethod
                            key={method}
                            selected={selectedShippingMethod === method}
                            onClick={() => setSelectedShippingMethod(method)}
                          >
                            <MethodHeader>
                              <MethodName>{details.name}</MethodName>
                              <MethodPrice>{details.currency} {details.cost}</MethodPrice>
                            </MethodHeader>
                            <MethodDescription>{details.description}</MethodDescription>
                          </ShippingMethod>
                        ))}
                      </ShippingMethods>
                    )}

                    <CalculateButton
                      onClick={calculateShipping}
                      disabled={!canCalculateShipping() || isLoadingShipping}
                    >
                      {isLoadingShipping ? (
                        <>
                          <div style={{ width: '16px', height: '16px', border: '2px solid #ffffff', borderTop: '2px solid transparent', borderRadius: '50%', animation: 'spin 1s linear infinite' }} />
                          Calculating...
                        </>
                      ) : (
                        <>
                          <FaCalculator />
                          Calculate Shipping
                        </>
                      )}
                    </CalculateButton>

                    {shippingCalculated && (
                      <ShippingSummary>
                        <SummaryRow>
                          <SummaryLabel>Subtotal:</SummaryLabel>
                          <SummaryValue>${getCartTotal().toFixed(2)}</SummaryValue>
                        </SummaryRow>
                        <SummaryRow>
                          <SummaryLabel>Shipping ({getShippingRates(shippingAddress.country)[selectedShippingMethod].name}):</SummaryLabel>
                          <SummaryValue>{getShippingRates(shippingAddress.country)[selectedShippingMethod].currency} {shippingCost}</SummaryValue>
                        </SummaryRow>
                        <SummaryRow>
                          <SummaryLabel>Total:</SummaryLabel>
                          <TotalWithShipping>${getTotalWithShipping().toFixed(2)}</TotalWithShipping>
                        </SummaryRow>
                      </ShippingSummary>
                    )}
                  </ShippingSection>

                  {shippingCalculated && shippingCost > 0 && (
                    <PayPalButtons
                      createOrder={(data, actions) => {
                        console.log('🚀 Creating PayPal order...');
                        console.log('💰 Total with shipping:', getTotalWithShipping());
                        console.log('📦 Cart items:', cart.length);
                        console.log('🚚 Shipping cost:', shippingCost);

                        // Validate total amount
                        const total = getTotalWithShipping();
                        if (total <= 0 || isNaN(total)) {
                          console.error('❌ Invalid total amount:', total);
                          alert('Invalid order total. Please try again.');
                          return Promise.reject(new Error('Invalid total amount'));
                        }

                        // Simple, reliable order structure
                        const orderData = {
                          purchase_units: [
                            {
                              amount: {
                                value: total.toFixed(2),
                                currency_code: "USD",
                                breakdown: {
                                  item_total: {
                                    currency_code: "USD",
                                    value: getCartTotal().toFixed(2)
                                  },
                                  shipping: {
                                    currency_code: "USD",
                                    value: shippingCost.toFixed(2)
                                  }
                                }
                              },
                              description: `Smoking Snakes Merchandise - ${cart.length} items`,
                              custom_id: `cart-${Date.now()}`,
                              items: [
                                ...cart.map(item => ({
                                  name: item.title,
                                  quantity: item.quantity.toString(),
                                  unit_amount: {
                                    currency_code: "USD",
                                    value: item.priceUSD.toFixed(2)
                                  },
                                  category: "PHYSICAL_GOODS"
                                })),
                                {
                                  name: "Shipping & Handling",
                                  quantity: "1",
                                  unit_amount: {
                                    currency_code: "USD",
                                    value: shippingCost.toFixed(2)
                                  },
                                  category: "SHIPPING"
                                }
                              ]
                            }
                          ]
                        };

                        console.log('📋 Final order data:', orderData);

                        return actions.order.create(orderData);
                      }}
                      onApprove={(data, actions) => {
                        return actions.order.capture().then((details) => {
                          setOrderComplete(true);
                          setOrderDetails({
                            item: `Cart with ${cart.length} items`,
                            orderID: details.id,
                            payerID: details.payer.payer_id
                          });
                          setCart([]);
                          setCartOpen(false);
                          // Reset shipping
                          setShippingCost(0);
                          setShippingCalculated(false);
                          setSelectedShippingMethod('standard');
                          setShippingAddress({
                            country: 'sweden',
                            city: '',
                            postalCode: '',
                            address: ''
                          });
                        });
                      }}
                      onError={(err) => {
                        console.error("💥 PayPal error:", err);
                        console.error("💥 Error details:", JSON.stringify(err, null, 2));
                        alert(`PayPal Error: ${err.message || 'Unknown error occurred'}`);
                      }}
                      style={{ layout: "horizontal" }}
                    />
                  )}
                </CartTotal>
              </>
            )}
          </CartContent>
        </CartModal>
      )}

      {/* Order Success Message */}
      {orderComplete && orderDetails && (
        <OrderSuccess style={{ position: 'fixed', top: '20px', right: '20px', zIndex: 3000 }}>
          ✅ Order Complete! Order ID: {orderDetails.orderID}
        </OrderSuccess>
      )}
    </MerchandiseContainer>
  );
};

export default Merchandise;
