import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaTshirt, FaHatCowboy, FaGuitar, FaGem, FaFire, FaCircle, FaMusic, FaShoppingCart } from 'react-icons/fa';

const MerchandiseContainer = styled.div`
  min-height: 100vh;
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const PageTitle = styled(motion.h1)`
  font-size: 3rem;
  text-align: center;
  margin-bottom: 3rem;
  color: #ff0000;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const HeroSection = styled.section`
  text-align: center;
  margin-bottom: 4rem;
  padding: 3rem 2rem;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const HeroTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const HeroDescription = styled.p`
  font-size: 1.2rem;
  color: #cccccc;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const MerchandiseGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
  margin-bottom: 4rem;
`;

const MerchandiseCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 16px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &:hover {
    transform: translateY(-5px);
    border-color: #ff0000;
    box-shadow: 0 10px 30px rgba(255, 0, 0, 0.2);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(45deg, #ff0000, #cc0000);
    transform: scaleX(0);
    transition: transform 0.3s ease;
  }

  &:hover::before {
    transform: scaleX(1);
  }
`;

const MerchandiseImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 12px;
  margin-bottom: 1.5rem;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const MerchandiseTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const MerchandiseCategory = styled.div`
  color: #ff6666;
  font-weight: 600;
  font-size: 0.9rem;
  margin-bottom: 1rem;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const MerchandisePrice = styled.div`
  font-size: 1.8rem;
  font-weight: 700;
  color: #64ffda;
  margin-bottom: 1.5rem;
`;

const MerchandiseDescription = styled.p`
  color: #cccccc;
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const BuyButton = styled.a`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  text-decoration: none;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.3);
  }
`;

const SoldOutBadge = styled.div`
  background: #666666;
  color: #ffffff;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  font-weight: 600;
  text-align: center;
  margin-top: 1rem;
`;

const Merchandise = () => {
  const merchandiseItems = [
    {
      id: 1,
      title: "Danger Zone T-Shirt",
      category: "Shirt",
      price: "€30 EUR",
      description: "Official Smoking Snakes 'Danger Zone' T-Shirt featuring the album artwork. High-quality cotton with the band's signature design.",
      icon: <FaTshirt />,
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 2,
      title: "RAWK Guitar Pic Bundle",
      category: "Button/Pin/Patch",
      price: "€6 EUR",
      description: "Smoking Snakes RAWK guitar pick bundle. Perfect for guitarists and collectors. Multiple picks with the band's logo.",
      icon: <FaGuitar />,
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 3,
      title: "Military Identification Necklace",
      category: "Other Apparel",
      price: "€20 EUR",
      description: "Unique Smoking Snakes Military Identification Necklace. A distinctive piece of band merchandise with military-style design.",
      icon: <FaGem />,
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 4,
      title: "Classic Logo Snapback Trucker Cap",
      category: "Hat",
      price: "€25 EUR",
      description: "Classic Smoking Snakes logo snapback trucker cap. Comfortable fit with embroidered band logo. Perfect for any occasion.",
      icon: <FaHatCowboy />,
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 5,
      title: "Classic Logo Patch – No Frills, Just Filth",
      category: "Other Apparel",
      price: "€8 EUR",
      description: "🐍 Classic Smoking Snakes logo patch. No frills, just filth. Perfect for jackets, bags, or anywhere you want to show your support.",
      icon: <FaCircle />,
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 6,
      title: "Scandinavian Premium Sleaze Metal – Limited Patch Drop!",
      category: "Other Apparel",
      price: "€9 EUR",
      description: "🔥 Scandinavian Premium Sleaze Metal – Limited edition patch drop! Exclusive design celebrating the band's Swedish heritage and sleaze metal style.",
      icon: <FaFire />,
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 7,
      title: "Danger Zone - Red LP",
      category: "Vinyl",
      price: "€30 EUR",
      description: "Danger Zone - Red LP vinyl record. Limited edition red vinyl pressing of the debut album. A must-have for vinyl collectors and fans.",
      icon: <FaMusic />,
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 8,
      title: "Classic Logo T-shirt",
      category: "Shirt",
      price: "€30 EUR",
      description: "Classic Smoking Snakes logo T-shirt. The iconic band logo on high-quality cotton. A timeless piece of band merchandise.",
      icon: <FaTshirt />,
      available: false,
      link: "https://smokingsnakes.bandcamp.com/merch"
    }
  ];

  return (
    <MerchandiseContainer>
      <PageTitle
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        Merchandise
      </PageTitle>

      <HeroSection>
        <HeroTitle>Official Smoking Snakes Merchandise</HeroTitle>
        <HeroDescription>
          Show your support for the band with our exclusive merchandise collection. 
          From clothing to accessories, we've got everything a true Smoking Snakes fan needs.
        </HeroDescription>
      </HeroSection>

      <MerchandiseGrid>
        {merchandiseItems.map((item, index) => (
          <MerchandiseCard
            key={item.id}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: index * 0.1 }}
          >
            <MerchandiseImage>
              {item.icon}
            </MerchandiseImage>

            <MerchandiseTitle>
              {item.title}
            </MerchandiseTitle>

            <MerchandiseCategory>
              {item.category}
            </MerchandiseCategory>

            <MerchandisePrice>
              {item.price}
            </MerchandisePrice>

            <MerchandiseDescription>
              {item.description}
            </MerchandiseDescription>

            {item.available ? (
              <BuyButton href={item.link} target="_blank" rel="noopener noreferrer">
                <FaShoppingCart />
                Buy Now
              </BuyButton>
            ) : (
              <SoldOutBadge>SOLD OUT</SoldOutBadge>
            )}
          </MerchandiseCard>
        ))}
      </MerchandiseGrid>
    </MerchandiseContainer>
  );
};

export default Merchandise;
