import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { 
  FaTshirt, 
  FaHatCowboy, 
  FaMusic, 
  FaGuitar, 
  FaGem, 
  FaCircle,
  FaExternalLinkAlt
} from 'react-icons/fa';

/*
  BANDCAMP INTEGRATION:

  Simple merchandise display with direct links to Bandcamp.
  Users can view merchandise and click to purchase on Bandcamp.
  No cart or checkout functionality - direct redirect to Bandcamp store.
*/

const MerchandiseContainer = styled.div`
  min-height: 100vh;
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const PageTitle = styled(motion.h1)`
  font-size: 3rem;
  text-align: center;
  margin-bottom: 3rem;
  color: #ff0000;
  font-family: 'Orbitron', 'Arial Black', sans-serif;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 3px;
  text-shadow: 0 0 20px rgba(255, 0, 0, 0.5);

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const HeroSection = styled.section`
  text-align: center;
  margin-bottom: 4rem;
  padding: 3rem 2rem;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const HeroTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const HeroDescription = styled.p`
  font-size: 1.2rem;
  color: #cccccc;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const MerchandiseGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
  margin-bottom: 4rem;
`;

const MerchandiseCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 16px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &:hover {
    transform: translateY(-5px);
    border-color: #ff0000;
    box-shadow: 0 10px 30px rgba(255, 0, 0, 0.2);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(45deg, #ff0000, #cc0000);
    transform: scaleX(0);
    transition: transform 0.3s ease;
  }

  &:hover::before {
    transform: scaleX(1);
  }
`;

const MerchandiseImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 12px;
  margin-bottom: 1.5rem;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const MerchandiseTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const MerchandiseCategory = styled.div`
  color: #ff6666;
  font-weight: 600;
  font-size: 0.9rem;
  margin-bottom: 1rem;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const MerchandisePrice = styled.div`
  font-size: 1.8rem;
  font-weight: 700;
  color: #64ffda;
  margin-bottom: 1.5rem;
`;

const MerchandiseDescription = styled.p`
  color: #cccccc;
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const BuyButton = styled.a`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  text-decoration: none;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.3);
  }
`;

const SoldOutBadge = styled.div`
  background: #666666;
  color: #ffffff;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  font-weight: 600;
  text-align: center;
  margin-top: 1rem;
`;

const CheckoutSection = styled.div`
  margin-top: 2rem;
  padding: 2rem;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.03) 0%, rgba(0, 0, 0, 0.1) 100%);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const CheckoutTitle = styled.h3`
  color: #ffffff;
  font-size: 1.4rem;
  margin-bottom: 1.5rem;
  text-align: center;
  font-family: 'Orbitron', 'Arial Black', sans-serif;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const OrderSummary = styled.div`
  background: rgba(255, 255, 255, 0.02);
  border-radius: 12px;
  padding: 1.5rem;
  margin-bottom: 1.5rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const SummaryRow = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.8rem;
  padding: 0.5rem 0;

  &:last-child {
    margin-bottom: 0;
    padding-top: 1rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    font-weight: 700;
    font-size: 1.2rem;
    color: #ff0000;
  }
`;

const SummaryLabel = styled.span`
  color: #cccccc;
  font-size: 1rem;
`;

const SummaryValue = styled.span`
  color: #ffffff;
  font-weight: 600;
`;

const QuantityControl = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  background: rgba(255, 255, 255, 0.05);
  padding: 0.5rem;
  border-radius: 25px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const QuantityButton = styled.button`
  background: rgba(255, 0, 0, 0.2);
  border: none;
  color: #ffffff;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 0, 0, 0.4);
    transform: scale(1.1);
  }
`;

const QuantityDisplay = styled.span`
  color: #ffffff;
  font-weight: 600;
  min-width: 30px;
  text-align: center;
`;

const RemoveButton = styled.button`
  background: rgba(255, 0, 0, 0.2);
  border: 1px solid rgba(255, 0, 0, 0.3);
  color: #ffffff;
  padding: 0.5rem 1rem;
  border-radius: 20px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.9rem;

  &:hover {
    background: rgba(255, 0, 0, 0.4);
    border-color: #ff0000;
  }
`;

const CartTotal = styled.div`
  padding: 2rem;
  background: linear-gradient(135deg, rgba(255, 0, 0, 0.05) 0%, rgba(0, 0, 0, 0.1) 100%);
  border-radius: 0 0 20px 20px;
`;

const TotalAmount = styled.div`
  color: #ffffff;
  font-size: 1.5rem;
  font-weight: 700;
  text-align: center;
  margin-bottom: 1.5rem;
  padding: 1rem;
  background: rgba(255, 0, 0, 0.1);
  border-radius: 12px;
  border: 1px solid rgba(255, 0, 0, 0.3);
`;

const EmptyCart = styled.div`
  text-align: center;
  padding: 3rem 2rem;
  color: #cccccc;

  h3 {
    color: #ffffff;
    margin-bottom: 1rem;
    font-size: 1.5rem;
  }

  p {
    font-size: 1.1rem;
    line-height: 1.6;
  }
`;



const SizeSelector = styled.div`
  margin-bottom: 1rem;
`;

const SizeLabel = styled.label`
  display: block;
  color: #cccccc;
  margin-bottom: 0.5rem;
  font-size: 0.9rem;
`;

const SizeOptions = styled.div`
  display: flex;
  gap: 0.5rem;
  flex-wrap: wrap;
`;

const SizeButton = styled.button`
  padding: 0.5rem 1rem;
  border: 1px solid ${props => props.selected ? '#ff0000' : '#666666'};
  background: ${props => props.selected ? '#ff0000' : 'transparent'};
  color: ${props => props.selected ? '#ffffff' : '#cccccc'};
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.9rem;

  &:hover {
    border-color: #ff0000;
    background: rgba(255, 0, 0, 0.1);
  }
`;

const AddToCartButton = styled.button`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  border: none;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 1rem;
  width: 100%;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.3);
  }

  &:disabled {
    background: #666666;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

const CartIcon = styled.div`
  position: fixed;
  top: 2rem;
  right: 2rem;
  background: linear-gradient(135deg, #ff0000 0%, #cc0000 100%);
  color: white;
  width: 70px;
  height: 70px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 1.8rem;
  box-shadow: 0 8px 25px rgba(255, 0, 0, 0.4);
  transition: all 0.3s ease;
  z-index: 9999;
  animation: pulse 2s infinite;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 35px rgba(255, 0, 0, 0.6);
    animation: none;
  }

  @media (max-width: 768px) {
    top: 1rem;
    right: 1rem;
    width: 60px;
    height: 60px;
    font-size: 1.5rem;
  }

  @keyframes pulse {
    0% {
      box-shadow: 0 8px 25px rgba(255, 0, 0, 0.4);
    }
    50% {
      box-shadow: 0 8px 25px rgba(255, 0, 0, 0.8);
    }
    100% {
      box-shadow: 0 8px 25px rgba(255, 0, 0, 0.4);
    }
  }
`;

const CartLabel = styled.div`
  position: fixed;
  top: 5.5rem;
  right: 2rem;
  color: #ffffff;
  font-size: 0.8rem;
  font-weight: 600;
  text-align: center;
  z-index: 9999;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.8);

  @media (max-width: 768px) {
    top: 4.5rem;
    right: 1rem;
    font-size: 0.7rem;
  }
`;

const CartBadge = styled.div`
  position: absolute;
  top: -8px;
  right: -8px;
  background: #ffffff;
  color: #ff0000;
  border-radius: 50%;
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.8rem;
  font-weight: 700;
  border: 2px solid #ff0000;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
`;

const CartModal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 0.5rem;
`;

const CartContent = styled.div`
  background: linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%);
  border-radius: 16px;
  max-width: 600px;
  width: 100%;
  max-height: 85vh;
  overflow-y: auto;
  border: 2px solid rgba(255, 0, 0, 0.3);
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.8);
  position: relative;
`;

const CartHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem 1.5rem 1rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  background: linear-gradient(135deg, rgba(255, 0, 0, 0.1) 0%, rgba(0, 0, 0, 0.2) 100%);
  border-radius: 16px 16px 0 0;
`;

const CartHeaderLeft = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const CartTitle = styled.h2`
  color: #ffffff;
  font-size: 1.5rem;
  font-weight: 700;
  margin: 0;
  font-family: 'Orbitron', 'Arial Black', sans-serif;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const CartSubtitle = styled.p`
  color: #cccccc;
  font-size: 0.9rem;
  margin: 0;
  font-style: italic;
`;

const CartHeaderRight = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #ffffff;
  font-size: 1.5rem;
  cursor: pointer;
  padding: 0.5rem;
  border-radius: 50%;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 0, 0, 0.2);
    transform: scale(1.1);
  }
`;

const CartItem = styled.div`
  display: flex;
  align-items: center;
  padding: 0.75rem 1rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  transition: background 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.02);
  }

  &:last-child {
    border-bottom: none;
  }
`;

const CartItemImage = styled.img`
  width: 80px;
  height: 80px;
  object-fit: cover;
  border-radius: 12px;
  border: 2px solid rgba(255, 0, 0, 0.3);

  @media (max-width: 768px) {
    width: 120px;
    height: 120px;
    margin: 0 auto;
  }
`;

const CartItemImageFallback = styled.div`
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, #1a0000 0%, #2d0000 50%, #400000 100%);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #ff0000;
  font-size: 2rem;
  border: 2px solid rgba(255, 0, 0, 0.3);

  @media (max-width: 768px) {
    width: 120px;
    height: 120px;
    margin: 0 auto;
  }
`;

const CartItemDetails = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const CartItemTitle = styled.h4`
  color: #ffffff;
  font-size: 1.2rem;
  margin: 0;
  font-weight: 600;
`;

const CartItemSize = styled.span`
  color: #cccccc;
  font-size: 0.9rem;
  background: rgba(255, 0, 0, 0.1);
  padding: 0.3rem 0.8rem;
  border-radius: 20px;
  display: inline-block;
  border: 1px solid rgba(255, 0, 0, 0.3);
`;

const CartItemPrice = styled.span`
  color: #ff0000;
  font-size: 1.1rem;
  font-weight: 700;
`;

const CartSummary = styled.div`
  padding: 1.5rem;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.02) 0%, rgba(0, 0, 0, 0.05) 100%);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
`;

const CartSummaryTitle = styled.h3`
  color: #ffffff;
  font-size: 1.2rem;
  margin-bottom: 1rem;
  font-family: 'Orbitron', 'Arial Black', sans-serif;
  text-transform: uppercase;
  letter-spacing: 1px;
  text-align: center;
`;

const CartSummaryGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 2rem;
  align-items: start;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 1rem;
  }
`;

const CartItemsSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const CartSummarySection = styled.div`
  background: rgba(255, 255, 255, 0.03);
  border-radius: 12px;
  padding: 1.5rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
  min-width: 300px;

  @media (max-width: 768px) {
    min-width: auto;
  }
`;

const SummarySectionTitle = styled.h4`
  color: #ffffff;
  font-size: 1.1rem;
  margin-bottom: 1rem;
  font-weight: 600;
  text-align: center;
`;



const Merchandise = () => {
  const merchandiseItems = [
    {
      id: 1,
      title: "Danger Zone T-Shirt",
      category: "Shirt",
      price: "‚Ç¨30 EUR",
      priceUSD: 32.50,
      description: "Official Smoking Snakes 'Danger Zone' T-Shirt featuring the album artwork. High-quality cotton with the band's signature design.",
      image: "/tshirt.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
