import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaPlay, FaDownload, FaMusic, FaCalendarAlt, FaClock, FaExternalLinkAlt } from 'react-icons/fa';
import { Helmet } from 'react-helmet-async';

const DiscographyContainer = styled.div`
  min-height: 100vh;
  padding: 0;
  max-width: 100%;
  margin: 0;
  background: #000;
  color: #fff;
`;

const PageTitle = styled(motion.h1)`
  font-size: 4rem;
  text-align: center;
  margin: 0;
  padding: 4rem 2rem 2rem 2rem;
  color: #fff;
  font-family: 'Arial Black', sans-serif;
  font-weight: 900;
  text-transform: uppercase;
  letter-spacing: 2px;
  background: linear-gradient(135deg, #000, #333);

  @media (max-width: 768px) {
    font-size: 2.5rem;
    padding: 3rem 1rem 1.5rem 1rem;
  }
`;

const AlbumGrid = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0;
  margin: 0 auto;
  max-width: 1000px;

  @media (max-width: 768px) {
    gap: 0;
    max-width: 100%;
  }
`;

const AlbumCard = styled(motion.div)`
  background: #000;
  border: none;
  padding: 0;
  margin: 0;
  position: relative;
  display: flex;
  flex-direction: row;
  align-items: center;
  min-height: 400px;
  border-bottom: 1px solid #333;

  &:last-child {
    border-bottom: none;
  }

  @media (max-width: 768px) {
    flex-direction: column;
    min-height: auto;
  }
`;

const AlbumCover = styled.div`
  width: 300px;
  height: 300px;
  background: linear-gradient(45deg, #000, #333);
  border-radius: 0;
  margin: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  flex-shrink: 0;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('${props => props.coverImage}') center/cover;
    opacity: 0.9;
  }

  @media (max-width: 768px) {
    width: 100%;
    height: 250px;
  }
`;

const AlbumContent = styled.div`
  flex: 1;
  padding: 3rem;
  text-align: left;
  max-width: 600px;

  @media (max-width: 768px) {
    padding: 2rem;
    text-align: center;
    max-width: 100%;
  }
`;

const AlbumTitle = styled.h3`
  font-size: 3rem;
  margin-bottom: 1rem;
  color: #fff;
  font-family: 'Arial Black', sans-serif;
  font-weight: 900;
  text-transform: uppercase;
  letter-spacing: 1px;
  line-height: 1.1;

  @media (max-width: 768px) {
    font-size: 2.2rem;
  }
`;

const AlbumYear = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: #ff0000;
  font-weight: 700;
  font-size: 1.3rem;
  margin-bottom: 1rem;
  font-family: 'Arial Black', sans-serif;
  text-transform: uppercase;
  letter-spacing: 2px;
  text-shadow: 0 0 10px rgba(255, 0, 0, 0.6);

  @media (max-width: 768px) {
    justify-content: center;
  }
`;

const AlbumReleaseDate = styled.div`
  color: #ccc;
  font-size: 1.1rem;
  margin-bottom: 1.5rem;
  font-family: Arial, sans-serif;
  font-style: italic;
  text-align: left;

  @media (max-width: 768px) {
    text-align: center;
  }
`;

const AlbumDescription = styled.div`
  color: #ccc;
  font-size: 1.1rem;
  line-height: 1.7;
  margin-bottom: 2rem;
  font-family: Arial, sans-serif;
  text-align: left;
  max-width: 600px;

  @media (max-width: 768px) {
    text-align: center;
    margin-left: auto;
    margin-right: auto;
  }
`;

const AlbumInfo = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  gap: 2rem;
  margin-bottom: 2rem;
  color: #ccc;
  font-size: 1rem;
  font-family: Arial, sans-serif;
  font-weight: 600;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    justify-content: center;
  }
`;

const TrackList = styled.div`
  margin-bottom: 0;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  padding: 1.5rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const TrackItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.8rem 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  color: #cccccc;
  font-size: 0.9rem;
  font-family: 'Inter', sans-serif;
  transition: all 0.3s ease;

  &:last-child {
    border-bottom: none;
  }

  &:hover {
    color: #ffffff;
    background: rgba(255, 255, 255, 0.05);
    border-radius: 8px;
    padding-left: 0.5rem;
    padding-right: 0.5rem;
  }
`;

const TrackNumber = styled.span`
  color: #ff0000;
  font-weight: 700;
  min-width: 30px;
  font-size: 1rem;
`;

const TrackTitle = styled.span`
  flex: 1;
  margin-left: 1rem;
  font-weight: 500;
`;

const TrackDuration = styled.span`
  color: #888;
  font-size: 0.9rem;
  font-weight: 500;
`;

const AlbumActions = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: center;
`;

const ActionButton = styled.button`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  border: none;
  padding: 0.8rem 1.5rem;
  font-size: 0.9rem;
  font-weight: 600;
  border-radius: 25px;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.3);
  }

  &.secondary {
    background: transparent;
    border: 1px solid #ff0000;
    color: #ff0000;

    &:hover {
      background: #ff0000;
      color: #ffffff;
    }
  }
`;

const ReviewsSection = styled.section`
  margin-top: 3rem;
  max-width: 900px;
  margin-left: auto;
  margin-right: auto;
`;

const ReviewsTitle = styled(motion.h2)`
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 2rem;
  color: #ff0000;
  font-family: 'Orbitron', 'Arial Black', sans-serif;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 2px;
  text-shadow: 0 0 15px rgba(255, 0, 0, 0.3);
`;

const ReviewCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.08);
  border-radius: 20px;
  padding: 2.5rem;
  border: 1px solid rgba(255, 255, 255, 0.15);
  margin-bottom: 2rem;
  position: relative;
  overflow: hidden;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.4);
    background: rgba(255, 255, 255, 0.1);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(45deg, #ff0000, #cc0000);
  }
`;

const ReviewHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1.5rem;

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
  }
`;

const ReviewLogo = styled.div`
  width: 70px;
  height: 70px;
  background: linear-gradient(45deg, #ff0000, #cc0000);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  font-size: 1.4rem;
  color: #ffffff;
  flex-shrink: 0;
  box-shadow: 0 4px 15px rgba(255, 0, 0, 0.3);
`;

const ReviewInfo = styled.div`
  flex: 1;
`;

const ReviewSource = styled.h3`
  color: #ffffff;
  font-size: 1.6rem;
  margin-bottom: 0.5rem;
  font-family: 'Orbitron', 'Arial Black', sans-serif;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const ReviewDate = styled.p`
  color: #cccccc;
  font-size: 0.9rem;
  margin: 0;
  font-style: italic;
`;

const ReviewText = styled.div`
  color: #cccccc;
  line-height: 1.7;
  font-size: 1.1rem;
  margin-bottom: 1.5rem;
  text-align: justify;
`;

const ReviewQuote = styled.blockquote`
  color: #ff0000;
  font-size: 1.2rem;
  font-style: italic;
  margin: 1.5rem 0;
  padding: 1.5rem;
  border-left: 4px solid #ff0000;
  background: rgba(255, 0, 0, 0.1);
  border-radius: 0 12px 12px 0;
  font-weight: 500;
`;

const ReviewLink = styled.a`
  color: #ff0000;
  text-decoration: none;
  font-weight: 600;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    color: #ffffff;
    text-shadow: 0 0 10px rgba(255, 0, 0, 0.5);
  }
`;

const Discography = () => {
  const albums = [
    {
      id: 1,
      title: "Restless & Wild",
      year: "2023",
      type: "Debut EP",
      trackCount: 3,
      duration: "12:45",
