import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const AboutContainer = styled.div`
  min-height: 100vh;
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const PageTitle = styled(motion.h1)`
  text-align: center;
  margin-bottom: 3rem;
  color: #ffffff;
  font-size: 3rem;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const HeroSection = styled.section`
  text-align: center;
  margin-bottom: 4rem;
  padding: 3rem 2rem;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const HeroTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const HeroDescription = styled.p`
  font-size: 1.2rem;
  color: #cccccc;
  max-width: 800px;
  margin: 0 auto;
  line-height: 1.6;
`;

const StorySection = styled.section`
  margin-bottom: 4rem;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 2rem;
  color: #ff0000;
`;

const StoryContent = styled.div`
  background: rgba(255, 255, 255, 0.03);
  border-radius: 16px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
  line-height: 1.8;
`;

const StoryParagraph = styled.p`
  color: #cccccc;
  margin-bottom: 1.5rem;
  font-size: 1.1rem;

  &:last-child {
    margin-bottom: 0;
  }
`;

const MembersSection = styled.section`
  margin-bottom: 4rem;
`;

const MembersGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
`;

const MemberCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 16px;
  padding: 2rem;
  text-align: center;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    border-color: #ff0000;
    box-shadow: 0 10px 30px rgba(255, 0, 0, 0.2);
  }
`;

const MemberAvatar = styled.div`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  background: linear-gradient(45deg, #ff0000, #cc0000);
  margin: 0 auto 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
  color: #ffffff;
`;

const MemberName = styled.h3`
  font-size: 1.8rem;
  margin-bottom: 0.5rem;
  color: #ffffff;
`;

const MemberRole = styled.p`
  color: #ff0000;
  font-weight: 500;
  margin-bottom: 1rem;
`;

const MemberBio = styled.p`
  color: #cccccc;
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const MemberInstruments = styled.div`
  display: flex;
  justify-content: center;
  gap: 0.5rem;
  flex-wrap: wrap;
`;

const InstrumentTag = styled.span`
  background: rgba(255, 0, 0, 0.2);
  color: #ff0000;
  padding: 0.3rem 0.8rem;
  border-radius: 12px;
  font-size: 0.9rem;
  font-weight: 500;
`;

const About = () => {
  const members = [
    {
      name: "Alex Johnson",
      role: "Lead Vocalist & Guitar",
      bio: "The charismatic frontman with a voice that can move mountains and lyrics that touch souls.",
      instruments: ["Vocals", "Guitar", "Piano"],
      avatar: "üé§"
    },
    {
      name: "Sarah Chen",
      role: "Lead Guitarist",
      bio: "A virtuoso guitarist who creates unforgettable riffs and mind-blowing solos.",
      instruments: ["Electric Guitar", "Acoustic Guitar", "Bass"],
      avatar: "üé∏"
    },
    {
      name: "Mike Rodriguez",
      role: "Bassist",
      bio: "The backbone of the band, providing the groove that makes everyone move.",
      instruments: ["Bass Guitar", "Backing Vocals"],
      avatar: "üé∏"
    },
    {
      name: "David Kim",
      role: "Drummer",
      bio: "A powerhouse drummer with incredible energy and precision that drives the band forward.",
      instruments: ["Drums", "Percussion"],
      avatar: "ü•Å"
    }
  ];

  const timeline = [
    {
      year: "2018",
      title: "Band Formation",
      description: "Four friends came together with a shared dream of creating music that would inspire and move people."
    },
    {
      year: "2019",
      title: "First EP Release",
      description: "Released our debut EP 'First Light' which gained local recognition and built our first fanbase."
    },
    {
      year: "2020",
      title: "First Light Album",
      description: "Our debut album 'First Light' was released, marking our official entry into the music industry."
    },
    {
      year: "2021",
      title: "First National Tour",
      description: "Embarked on our first national tour, performing in 15 cities across the country."
    },
    {
      year: "2022",
      title: "Rebel Heart Album",
      description: "Released 'Rebel Heart', our second album that showcased our growth and evolution as artists."
    },
    {
      year: "2023",
      title: "International Recognition",
      description: "Started receiving international recognition and performed at major music festivals worldwide."
    },
    {
      year: "2024",
      title: "Midnight Dreams",
      description: "Released our latest album 'Midnight Dreams', representing our most mature and powerful work yet."
    }
  ];

  const awards = [
    { title: "Best Rock Album", year: "2024", icon: "üèÜ" },
    { title: "Breakthrough Artist", year: "2022", icon: "‚≠ê" },
    { title: "Best Live Performance", year: "2023", icon: "üé≠" },
    { title: "Song of the Year", year: "2024", icon: "üéµ" }
  ];

  return (
    <AboutContainer>
      <PageTitle
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        About Us
      </PageTitle>

      <HeroSection>
        <HeroTitle>Our Story</HeroTitle>
        <HeroDescription>
          We are a passionate rock band that believes in the power of music to connect people, 
          inspire change, and create unforgettable moments. Our journey began with a simple dream 
          and has evolved into something extraordinary.
        </HeroDescription>
      </HeroSection>

      <StorySection>
        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          Our Journey
        </SectionTitle>
        <StoryContent>
          <StoryParagraph>
            It all started in a small garage in 2018, where four friends with a shared passion for rock music 
            decided to form a band. What began as weekend jam sessions quickly evolved into something much bigger.
          </StoryParagraph>
          <StoryParagraph>
            Our first EP, released in 2019, was recorded in that same garage with borrowed equipment and 
            a lot of determination. The response from our local community was overwhelming, and it gave us 
            the confidence to dream bigger.
          </StoryParagraph>
          <StoryParagraph>
            Over the years, we've grown not just as musicians, but as a family. We've learned that the 
            best music comes from authentic experiences, shared emotions, and a deep connection with our audience.
          </StoryParagraph>
          <StoryParagraph>
            Today, we continue to push boundaries, experiment with new sounds, and create music that 
            resonates with people around the world. Our journey is far from over, and we're excited to 
            see where the music takes us next.
          </StoryParagraph>
        </StoryContent>
      </StorySection>

      <MembersSection>
        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          Lineup:
        </SectionTitle>
        <MembersGrid>
          {members.map((member, index) => (
            <MemberCard
              key={index}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              viewport={{ once: true }}
            >
              <MemberAvatar>{member.avatar}</MemberAvatar>
              <MemberName>{member.name}</MemberName>
              <MemberRole>{member.role}</MemberRole>
              <MemberBio>{member.bio}</MemberBio>
              <MemberInstruments>
                {member.instruments.map((instrument, i) => (
                  <InstrumentTag key={i}>{instrument}</InstrumentTag>
                ))}
              </MemberInstruments>
            </MemberCard>
          ))}
        </MembersGrid>
      </MembersSection>
    </AboutContainer>
  );
};

export default About;
