        console.log(`ðŸ“¦ Fetching ${method} shipping rates...`);

        const url = `${POSTNORD_BASE_URL}/servicepoint/servicepoint?apikey=${POSTNORD_API_KEY}&countryCode=${country.toUpperCase()}&postalCode=${postalCode}&serviceCode=${serviceCode}`;
        console.log('ðŸ”— Request URL:', url);

        const response = await fetch(url, {
