          headers: {
            'Content-Type': 'application/json',
          }
        });

        console.log(`ðŸ“¡ Response status:`, response.status);
        console.log(`ðŸ“¡ Response ok:`, response.ok);

        if (response.ok) {
          const data = await response.json();
