import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaTshirt, FaHatCowboy, FaGuitar, FaGem, FaFire, FaCircle, FaMusic, FaShoppingCart, FaPaypal, FaTimes, FaMinus, FaPlus } from 'react-icons/fa';
import { PayPalButtons } from '@paypal/react-paypal-js';

const MerchandiseContainer = styled.div`
  min-height: 100vh;
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const PageTitle = styled(motion.h1)`
  font-size: 3rem;
  text-align: center;
  margin-bottom: 3rem;
  color: #ff0000;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const HeroSection = styled.section`
  text-align: center;
  margin-bottom: 4rem;
  padding: 3rem 2rem;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const HeroTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const HeroDescription = styled.p`
  font-size: 1.2rem;
  color: #cccccc;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const MerchandiseGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
  margin-bottom: 4rem;
`;

const MerchandiseCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 16px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &:hover {
    transform: translateY(-5px);
    border-color: #ff0000;
    box-shadow: 0 10px 30px rgba(255, 0, 0, 0.2);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(45deg, #ff0000, #cc0000);
    transform: scaleX(0);
    transition: transform 0.3s ease;
  }

  &:hover::before {
    transform: scaleX(1);
  }
`;

const MerchandiseImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 12px;
  margin-bottom: 1.5rem;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const MerchandiseTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const MerchandiseCategory = styled.div`
  color: #ff6666;
  font-weight: 600;
  font-size: 0.9rem;
  margin-bottom: 1rem;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const MerchandisePrice = styled.div`
  font-size: 1.8rem;
  font-weight: 700;
  color: #64ffda;
  margin-bottom: 1.5rem;
`;

const MerchandiseDescription = styled.p`
  color: #cccccc;
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const BuyButton = styled.a`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  text-decoration: none;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.3);
  }
`;

const SoldOutBadge = styled.div`
  background: #666666;
  color: #ffffff;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  font-weight: 600;
  text-align: center;
  margin-top: 1rem;
`;

const PayPalContainer = styled.div`
  margin-top: 1rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const PayPalLabel = styled.div`
  color: #cccccc;
  font-size: 0.9rem;
  text-align: center;
  margin-bottom: 0.5rem;
`;

const OrderSuccess = styled.div`
  background: rgba(0, 255, 0, 0.1);
  border: 1px solid rgba(0, 255, 0, 0.3);
  border-radius: 8px;
  padding: 1rem;
  margin-top: 1rem;
  text-align: center;
  color: #00ff00;
`;

const SizeSelector = styled.div`
  margin-bottom: 1rem;
`;

const SizeLabel = styled.label`
  display: block;
  color: #cccccc;
  margin-bottom: 0.5rem;
  font-size: 0.9rem;
`;

const SizeOptions = styled.div`
  display: flex;
  gap: 0.5rem;
  flex-wrap: wrap;
`;

const SizeButton = styled.button`
  padding: 0.5rem 1rem;
  border: 1px solid ${props => props.selected ? '#ff0000' : '#666666'};
  background: ${props => props.selected ? '#ff0000' : 'transparent'};
  color: ${props => props.selected ? '#ffffff' : '#cccccc'};
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.9rem;

  &:hover {
    border-color: #ff0000;
    background: rgba(255, 0, 0, 0.1);
  }
`;

const AddToCartButton = styled.button`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  border: none;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 1rem;
  width: 100%;
  justify-content: center;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.3);
  }

  &:disabled {
    background: #666666;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

const CartIcon = styled.div`
  position: fixed;
  top: 100px;
  right: 30px;
  background: #ff0000;
  color: white;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 1.5rem;
  box-shadow: 0 4px 15px rgba(255, 0, 0, 0.3);
  transition: all 0.3s ease;
  z-index: 1000;

  &:hover {
    transform: scale(1.1);
    box-shadow: 0 6px 20px rgba(255, 0, 0, 0.4);
  }
`;

const CartBadge = styled.div`
  position: absolute;
  top: -8px;
  right: -8px;
  background: #ffffff;
  color: #ff0000;
  border-radius: 50%;
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.8rem;
  font-weight: bold;
`;

const CartModal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 2000;
  padding: 2rem;
`;

const CartContent = styled.div`
  background: #1a1a1a;
  border-radius: 16px;
  padding: 2rem;
  max-width: 600px;
  width: 100%;
  max-height: 80vh;
  overflow-y: auto;
  border: 1px solid rgba(255, 0, 0, 0.3);
`;

const CartHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
`;

const CartTitle = styled.h2`
  color: #ffffff;
  margin: 0;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #cccccc;
  font-size: 1.5rem;
  cursor: pointer;
  padding: 0.5rem;

  &:hover {
    color: #ff0000;
  }
`;

const CartItem = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 1rem 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
`;

const CartItemImage = styled.img`
  width: 60px;
  height: 60px;
  object-fit: cover;
  border-radius: 8px;
`;

const CartItemDetails = styled.div`
  flex: 1;
`;

const CartItemTitle = styled.h4`
  color: #ffffff;
  margin: 0 0 0.5rem 0;
`;

const CartItemSize = styled.span`
  color: #cccccc;
  font-size: 0.9rem;
`;

const CartItemPrice = styled.span`
  color: #ff0000;
  font-weight: bold;
`;

const QuantityControl = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const QuantityButton = styled.button`
  background: rgba(255, 255, 255, 0.1);
  border: none;
  color: #ffffff;
  width: 30px;
  height: 30px;
  border-radius: 4px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: rgba(255, 0, 0, 0.3);
  }
`;

const QuantityDisplay = styled.span`
  color: #ffffff;
  min-width: 30px;
  text-align: center;
`;

const RemoveButton = styled.button`
  background: rgba(255, 0, 0, 0.2);
  border: 1px solid rgba(255, 0, 0, 0.3);
  color: #ff0000;
  padding: 0.5rem;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background: rgba(255, 0, 0, 0.3);
  }
`;

const CartTotal = styled.div`
  margin-top: 2rem;
  padding-top: 1rem;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  text-align: right;
`;

const TotalAmount = styled.div`
  color: #ffffff;
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 1rem;
`;

const CheckoutButton = styled.button`
  background: #ff0000;
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 8px;
  font-weight: 600;
  font-size: 1.1rem;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #cc0000;
    transform: translateY(-2px);
  }
`;

const EmptyCart = styled.div`
  text-align: center;
  color: #cccccc;
  padding: 2rem;
`;

const Merchandise = () => {
  const [orderComplete, setOrderComplete] = useState(false);
  const [orderDetails, setOrderDetails] = useState(null);
  const [cart, setCart] = useState([]);
  const [cartOpen, setCartOpen] = useState(false);
  const [selectedSizes, setSelectedSizes] = useState({});

  // Cart functions
  const addToCart = (item) => {
    if (item.category === 'Shirt' && !selectedSizes[item.id]) {
      alert('Please select a size for this t-shirt');
      return;
    }

    const cartItem = {
      ...item,
      size: selectedSizes[item.id] || 'N/A',
      quantity: 1
    };

    setCart(prevCart => {
      const existingItem = prevCart.find(cartItem => 
        cartItem.id === item.id && cartItem.size === (selectedSizes[item.id] || 'N/A')
      );

      if (existingItem) {
        return prevCart.map(cartItem => 
          cartItem.id === item.id && cartItem.size === (selectedSizes[item.id] || 'N/A')
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        );
      } else {
        return [...prevCart, cartItem];
      }
    });

    // Reset size selection for this item
    setSelectedSizes(prev => ({ ...prev, [item.id]: undefined }));
  };

  const removeFromCart = (itemId, size) => {
    setCart(prevCart => prevCart.filter(item => 
      !(item.id === itemId && item.size === size)
    ));
  };

  const updateQuantity = (itemId, size, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(itemId, size);
      return;
    }

    setCart(prevCart => prevCart.map(item => 
      item.id === itemId && item.size === size
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const getCartTotal = () => {
    return cart.reduce((total, item) => total + (item.priceUSD * item.quantity), 0);
  };

  const getCartItemCount = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const merchandiseItems = [
    {
      id: 1,
      title: "Danger Zone T-Shirt",
      category: "Shirt",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Official Smoking Snakes 'Danger Zone' T-Shirt featuring the album artwork. High-quality cotton with the band's signature design.",
      image: "/merchandise/danger-zone-tshirt.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch",
      sizes: ["XS", "S", "M", "L", "XL", "XXL"]
    },
    {
      id: 2,
      title: "RAWK Guitar Pic Bundle",
      category: "Button/Pin/Patch",
      price: "€6 EUR",
      priceUSD: 6.50,
      description: "Smoking Snakes RAWK guitar pick bundle. Perfect for guitarists and collectors. Multiple picks with the band's logo.",
      image: "/merchandise/guitar-picks.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 3,
      title: "Military Identification Necklace",
      category: "Other Apparel",
      price: "€20 EUR",
      priceUSD: 21.70,
      description: "Unique Smoking Snakes Military Identification Necklace. A distinctive piece of band merchandise with military-style design.",
      image: "/merchandise/military-necklace.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 4,
      title: "Classic Logo Snapback Trucker Cap",
      category: "Hat",
      price: "€25 EUR",
      priceUSD: 27.10,
      description: "Classic Smoking Snakes logo snapback trucker cap. Comfortable fit with embroidered band logo. Perfect for any occasion.",
      image: "/merchandise/snapback-cap.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 5,
      title: "Classic Logo Patch – No Frills, Just Filth",
      category: "Other Apparel",
      price: "€8 EUR",
      priceUSD: 8.70,
      description: "🐍 Classic Smoking Snakes logo patch. No frills, just filth. Perfect for jackets, bags, or anywhere you want to show your support.",
      image: "/merchandise/logo-patch.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 6,
      title: "Scandinavian Premium Sleaze Metal – Limited Patch Drop!",
      category: "Other Apparel",
      price: "€9 EUR",
      priceUSD: 9.75,
      description: "🔥 Scandinavian Premium Sleaze Metal – Limited edition patch drop! Exclusive design celebrating the band's Swedish heritage and sleaze metal style.",
      image: "/merchandise/sleaze-patch.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 7,
      title: "Danger Zone - Red LP",
      category: "Vinyl",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Danger Zone - Red LP vinyl record. Limited edition red vinyl pressing of the debut album. A must-have for vinyl collectors and fans.",
      image: "/merchandise/red-lp.jpg",
      available: true,
      link: "https://smokingsnakes.bandcamp.com/merch"
    },
    {
      id: 8,
      title: "Classic Logo T-shirt",
      category: "Shirt",
      price: "€30 EUR",
      priceUSD: 32.50,
      description: "Classic Smoking Snakes logo T-shirt. The iconic band logo on high-quality cotton. A timeless piece of band merchandise.",
      image: "/merchandise/classic-logo-tshirt.jpg",
      available: false,
      link: "https://smokingsnakes.bandcamp.com/merch",
      sizes: ["XS", "S", "M", "L", "XL", "XXL"]
    }
  ];

  return (
    <MerchandiseContainer>
      <PageTitle
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        Merchandise
      </PageTitle>

      <HeroSection>
        <HeroTitle>Official Smoking Snakes Merchandise</HeroTitle>
        <HeroDescription>
          Show your support for the band with our exclusive merchandise collection. 
          From clothing to accessories, we've got everything a true Smoking Snakes fan needs.
        </HeroDescription>
      </HeroSection>

      <MerchandiseGrid>
        {merchandiseItems.map((item, index) => (
          <MerchandiseCard
            key={item.id}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: index * 0.1 }}
          >
            <MerchandiseImage
              src={item.image}
              alt={item.title}
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'flex';
              }}
            />
            <div style={{ 
              display: 'none', 
              width: '100%', 
              height: '200px', 
              background: 'linear-gradient(135deg, #1a0000 0%, #2d0000 50%, #400000 100%)', 
              borderRadius: '12px', 
              alignItems: 'center', 
              justifyContent: 'center', 
              fontSize: '3rem', 
              color: '#ff0000' 
            }}>
              {item.category === 'Shirt' ? <FaTshirt /> : 
               item.category === 'Hat' ? <FaHatCowboy /> : 
               item.category === 'Vinyl' ? <FaMusic /> : 
               item.category === 'Button/Pin/Patch' ? <FaGuitar /> : 
               item.category === 'Other Apparel' ? <FaGem /> : <FaCircle />}
            </div>

            <MerchandiseTitle>
              {item.title}
            </MerchandiseTitle>

            <MerchandiseCategory>
              {item.category}
            </MerchandiseCategory>

            <MerchandisePrice>
              {item.price}
            </MerchandisePrice>

            <MerchandiseDescription>
              {item.description}
            </MerchandiseDescription>

            {item.available && (
              <>
                {item.category === 'Shirt' && (
                  <SizeSelector>
                    <SizeLabel>Select Size:</SizeLabel>
                    <SizeOptions>
                      {item.sizes.map(size => (
                        <SizeButton
                          key={size}
                          selected={selectedSizes[item.id] === size}
                          onClick={() => setSelectedSizes(prev => ({ ...prev, [item.id]: size }))}
                        >
                          {size}
                        </SizeButton>
                      ))}
                    </SizeOptions>
                  </SizeSelector>
                )}

                <AddToCartButton
                  onClick={() => addToCart(item)}
                  disabled={item.category === 'Shirt' && !selectedSizes[item.id]}
                >
                  <FaShoppingCart />
                  Add to Cart
                </AddToCartButton>

                <div style={{ textAlign: 'center', marginTop: '0.5rem' }}>
                  <small style={{ color: '#888888' }}>
                    Or <a href={item.link} target="_blank" rel="noopener noreferrer" style={{ color: '#ff0000', textDecoration: 'none' }}>buy on Bandcamp</a>
                  </small>
                </div>
              </>
            )}

            {!item.available && (
              <SoldOutBadge>SOLD OUT</SoldOutBadge>
            )}
          </MerchandiseCard>
        ))}
      </MerchandiseGrid>
    </MerchandiseContainer>
  );
};

export default Merchandise;
