  const [selectedSizes, setSelectedSizes] = useState({});
  const [orderComplete, setOrderComplete] = useState(false);
  const [orderDetails, setOrderDetails] = useState(null);

  // Shipping state
  const [shippingAddress, setShippingAddress] = useState({
    country: 'sweden',
    city: '',
    postalCode: '',
    address: ''
  });
  const [selectedShippingMethod, setSelectedShippingMethod] = useState('standard');
  const [shippingCost, setShippingCost] = useState(0);
  const [shippingCalculated, setShippingCalculated] = useState(false);
  const [isLoadingShipping, setIsLoadingShipping] = useState(false);

  // PostNord API configuration
  const POSTNORD_API_KEY = process.env.REACT_APP_POSTNORD_API_KEY || 'your-api-key-here';
  const POSTNORD_BASE_URL = 'https://api.postnord.com/rest/businesslocation/v1';
  const POSTNORD_ENABLED = POSTNORD_API_KEY && POSTNORD_API_KEY !== 'your-api-key-here';

  // PostNord shipping rates (fallback if API fails)
  const fallbackShippingRates = {
    sweden: {
      standard: { cost: 15, name: 'Standard Shipping', description: '3-5 business days' },
      express: { cost: 35, name: 'Express Shipping', description: '1-2 business days' }
    },
    nordic: {
      standard: { cost: 25, name: 'Standard Shipping', description: '5-7 business days' },
      express: { cost: 50, name: 'Express Shipping', description: '2-3 business days' }
    },
    europe: {
      standard: { cost: 35, name: 'Standard Shipping', description: '7-10 business days' },
      express: { cost: 75, name: 'Express Shipping', description: '3-5 business days' }
    },
    worldwide: {
      standard: { cost: 65, name: 'Standard Shipping', description: '14-21 business days' },
      express: { cost: 120, name: 'Express Shipping', description: '7-10 business days' }
    }
  };

  // Get PostNord shipping rates
  const getPostNordShippingRates = async (postalCode, country) => {
    try {
      setIsLoadingShipping(true);

      // PostNord service codes for different shipping types
      const services = {
        standard: 'BPAKKE', // Standard package
        express: 'EXPRESS'  // Express package
      };

      const rates = {};

      for (const [method, serviceCode] of Object.entries(services)) {
        const response = await fetch(
          `${POSTNORD_BASE_URL}/servicepoint/servicepoint?apikey=${POSTNORD_API_KEY}&countryCode=${country.toUpperCase()}&postalCode=${postalCode}&serviceCode=${serviceCode}`,
          {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            }
          }
        );

        if (response.ok) {
          const data = await response.json();

          // Extract shipping cost from PostNord response
          // Note: Actual response structure may vary, you'll need to adjust based on PostNord's API documentation
          if (data.servicePointInformation && data.servicePointInformation.servicePoints) {
            const servicePoint = data.servicePointInformation.servicePoints[0];

            // This is a placeholder - you'll need to check PostNord's actual response structure
            rates[method] = {
              cost: method === 'standard' ? 15 : 35, // Fallback costs
              name: method === 'standard' ? 'Standard Shipping' : 'Express Shipping',
              description: method === 'standard' ? '3-5 business days' : '1-2 business days',
              postnordData: servicePoint
            };
          }
        }
      }

      return rates;
    } catch (error) {
      console.error('Error fetching PostNord rates:', error);
      return null;
    } finally {
      setIsLoadingShipping(false);
    }
  };

  // Get shipping rates (PostNord API or fallback)
  const getShippingRates = (country) => {
    const zone = getShippingZone(country);
    return fallbackShippingRates[zone];
  };

  // Determine shipping zone based on country
  const getShippingZone = (country) => {
    if (country === 'sweden') return 'sweden';
    if (['norway', 'denmark', 'finland', 'iceland'].includes(country)) return 'nordic';
    if (['germany', 'france', 'uk', 'italy', 'spain', 'netherlands', 'belgium', 'austria', 'switzerland', 'poland'].includes(country)) return 'europe';
    return 'worldwide';
  };

  // Calculate shipping cost
  const calculateShipping = async () => {
    if (!shippingAddress.country || !shippingAddress.city || !shippingAddress.postalCode || !shippingAddress.address) {
      alert('Please fill in all shipping address fields');
      return;
    }

    // If no shipping method selected yet, show available methods
    if (!selectedShippingMethod) {
      // Don't calculate yet, just show the shipping methods
      return;
    }

    // Try to get PostNord rates first
    if (shippingAddress.country === 'sweden' || ['norway', 'denmark', 'finland'].includes(shippingAddress.country)) {
      const postnordRates = await getPostNordShippingRates(shippingAddress.postalCode, shippingAddress.country);

      if (postnordRates && postnordRates[selectedShippingMethod]) {
        const cost = postnordRates[selectedShippingMethod].cost;
        setShippingCost(cost);
        setShippingCalculated(true);
        return;
      }
    }

    // Fallback to static rates if PostNord API fails or for other countries
    const zone = getShippingZone(shippingAddress.country);
    const method = selectedShippingMethod;

    const cost = fallbackShippingRates[zone][method].cost;
    setShippingCost(cost);
    setShippingCalculated(true);
  };

  // Check if address is complete
  const isAddressComplete = () => {
    return shippingAddress.country && shippingAddress.city && shippingAddress.postalCode && shippingAddress.address;
  };

  // Check if ready to calculate shipping
