import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaPlay, FaSpotify, FaApple, FaYoutube, FaInstagram, FaTiktok } from 'react-icons/fa';
import ImageCarousel from '../components/ImageCarousel';

const HomeContainer = styled.div`
  min-height: 100vh;
`;

const HeroSection = styled.section`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
  overflow: hidden;
`;

const HeroContent = styled(motion.div)`
  max-width: 800px;
  padding: 2rem;
  position: relative;
  z-index: 3;
`;

const HeroTitle = styled(motion.h1)`
  margin-bottom: 1rem;
  display: flex;
  justify-content: center;

  @media (max-width: 768px) {
    transform: scale(0.8);
  }
`;

const HeroLogo = styled.img`
  height: 360px;
  width: auto;
  max-width: 1200px;

  @media (max-width: 768px) {
    height: 240px;
    max-width: 800px;
  }
`;

const HeroSubtitle = styled(motion.p)`
  font-size: 1.5rem;
  margin-bottom: 2rem;
  color: #cccccc;

  @media (max-width: 768px) {
    font-size: 1.2rem;
  }
`;

const CTAButton = styled(motion.button)`
  background: linear-gradient(45deg, #ff0000, #cc0000);
  color: #ffffff;
  border: none;
  padding: 1rem 2rem;
  font-size: 1.2rem;
  font-weight: 600;
  border-radius: 50px;
  cursor: pointer;
  margin: 0 1rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(255, 0, 0, 0.3);
  }

  @media (max-width: 768px) {
    margin: 0.5rem;
    padding: 0.8rem 1.5rem;
    font-size: 1rem;
  }
`;

const SocialLinks = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-top: 2rem;
`;

const SocialIcon = styled.a`
  color: #ffffff;
  font-size: 1.5rem;
  padding: 0.5rem;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;

  &:hover {
    color: #ff0000;
    background: rgba(255, 0, 0, 0.2);
    transform: translateY(-2px);
  }
`;

const FeaturedSection = styled.section`
  padding: 5rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 3rem;
  color: #ff0000;
`;

const FeaturedGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-bottom: 3rem;
`;

const FeaturedCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 16px;
  padding: 2rem;
  text-align: center;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    border-color: #ff0000;
    box-shadow: 0 10px 30px rgba(255, 0, 0, 0.2);
  }
`;

const CardIcon = styled.div`
  font-size: 3rem;
  color: #ff0000;
  margin-bottom: 1rem;
`;

const CardTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const CardDescription = styled.p`
  color: #cccccc;
  line-height: 1.6;
`;

const LatestRelease = styled.section`
  padding: 5rem 2rem;
  background: rgba(255, 255, 255, 0.02);
  text-align: center;
`;

const AlbumCover = styled(motion.div)`
  width: 300px;
  height: 300px;
  margin: 0 auto 2rem;
  background: linear-gradient(45deg, #ff0000, #cc0000);
  border-radius: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 4rem;
  color: #ffffff;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }

  @media (max-width: 768px) {
    width: 250px;
    height: 250px;
    font-size: 3rem;
  }
`;

const AlbumTitle = styled.h3`
  font-size: 2rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const AlbumDescription = styled.p`
  color: #cccccc;
  margin-bottom: 2rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const StreamingButtons = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  flex-wrap: wrap;
`;

const StreamingButton = styled.a`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  background: rgba(255, 255, 255, 0.1);
  color: #ffffff;
  text-decoration: none;
  padding: 0.8rem 1.5rem;
  border-radius: 25px;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 0, 0, 0.2);
    color: #ff0000;
    transform: translateY(-2px);
  }
`;

const Home = () => {
  return (
    <HomeContainer>
      <HeroSection>
        <ImageCarousel />
        <HeroContent
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >



        </HeroContent>
      </HeroSection>

      <LatestRelease>
        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          Debut Album
        </SectionTitle>
        <AlbumCover
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          style={{
            backgroundImage: 'url(/danger-zone-album-cover.jpg)',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat'
          }}
        />
        <AlbumTitle>"Danger Zone"</AlbumTitle>
        <AlbumDescription>
          "Danger Zone," the debut album from Swedish band Smoking Snakes, is a high-energy sleaze metal record that pays homage to 80s hard rock and metal, particularly bands like W.A.S.P., RATT, and Dokken. Reviews praise the album's catchy, anthemic choruses, driving rhythms, and electrifying guitar riffs, noting the band's youthful energy and strong musicianship. While some reviewers find the vocal delivery a bit shouty, the overall consensus is that "Danger Zone" is a solid and fun listen for fans of the genre. Available on all digital platforms.
        </AlbumDescription>
        <StreamingButtons>
          <StreamingButton href="https://open.spotify.com/artist/4QanEE5Wrkq1JhWMpWKz4f?si=tYmQ49OiQT-KxGhyFoTEmA" target="_blank">
            <FaSpotify />
            Spotify
          </StreamingButton>
          <StreamingButton href="https://music.apple.com/us/artist/smoking-snakes/1618982403" target="_blank">
            <FaApple />
            Apple Music
          </StreamingButton>
          <StreamingButton href="https://youtube.com/@smokingsnakesofsweden" target="_blank">
            <FaYoutube />
            YouTube
          </StreamingButton>
        </StreamingButtons>
      </LatestRelease>

      <FeaturedSection>
        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          Reviews
        </SectionTitle>
        <FeaturedGrid>
