  const SHOP_PAY_STORE_URL = process.env.REACT_APP_SHOP_PAY_STORE_URL || 'https://smoking-snakes.myshopify.com';
  const SHOP_PAY_COLLECTION_ID = process.env.REACT_APP_SHOP_PAY_COLLECTION_ID || 'your_collection_id';

  // Shop Pay utility functions
  const getShopPayCollectionUrl = () => {
    return `${SHOP_PAY_STORE_URL}/collections/${SHOP_PAY_COLLECTION_ID}`;
  };

  const getShopPayProductUrl = (productId) => {
    return `${SHOP_PAY_STORE_URL}/products/${productId}`;
  };

  // Shop Pay checkout function
  const redirectToShopPayCheckout = () => {
    if (cart.length === 0) {
      alert('Your cart is empty. Please add items before checkout.');
      return;
    }

    // Validate that all items have proper Shop Pay product IDs
    const invalidItems = cart.filter(item => {
      if (item.category === 'Shirt') {
        return !item.shopPayVariantId;
      }
      return !item.shopPayId;
    });

    if (invalidItems.length > 0) {
      alert('Some items in your cart are missing Shop Pay product information. Please contact support.');
      console.error('Invalid items:', invalidItems);
      return;
    }

    try {
      // Build Shop Pay checkout URL with cart items
      const baseUrl = `${SHOP_PAY_STORE_URL}/cart`;
      const cartItems = cart.map(item => {
        // Map our cart items to Shop Pay format
        const shopPayItem = {
          id: item.shopPayId || item.id, // Use Shop Pay product ID if available
          quantity: item.quantity
        };

        // Add variant ID for items with sizes (like shirts)
        if (item.category === 'Shirt' && item.shopPayVariantId) {
          shopPayItem.id = item.shopPayVariantId; // Use variant ID for shirts with sizes
        }

        return shopPayItem;
      });

      // Create checkout URL with additional parameters
      const checkoutParams = new URLSearchParams();

      // Add cart items
      cartItems.forEach(item => {
        checkoutParams.append('id', item.id);
        checkoutParams.append('quantity', item.quantity);
      });

      // Add additional Shop Pay parameters
      checkoutParams.append('source', 'band-website');
      checkoutParams.append('utm_source', 'band-website');
      checkoutParams.append('utm_medium', 'merchandise');
      checkoutParams.append('utm_campaign', 'smoking-snakes-merch');

      const checkoutUrl = `${baseUrl}?${checkoutParams.toString()}`;

      // Redirect to Shop Pay checkout
      window.open(checkoutUrl, '_blank');

      // Close cart modal
      setCartOpen(false);

      // Show success message
      alert('Redirecting to Shop Pay checkout...');

