      ...item,
      size: selectedSize,
      quantity: 1,
      // Add Shop Pay variant ID for shirts
      shopPayVariantId: item.category === 'Shirt' && item.shopPayVariants ? 
        item.shopPayVariants[selectedSize] : null
    };

