      const existingItem = prevCart.find(cartItem => 
        cartItem.id === item.id && cartItem.size === selectedSize
      );

      if (existingItem) {
        return prevCart.map(cartItem => 
          cartItem.id === item.id && cartItem.size === selectedSize
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        );
