              justifyContent: 'center', 
              fontSize: '3rem', 
              color: '#ff0000' 
            }}>
              {item.category === 'Shirt' ? <FaTshirt /> : 
               item.category === 'Hat' ? <FaHatCowboy /> : 
               item.category === 'Vinyl' ? <FaMusic /> : 
               item.category === 'Button/Pin/Patch' ? <FaGuitar /> : 
               item.category === 'Other Apparel' ? <FaGem /> : <FaCircle />}
            </div>

            <MerchandiseTitle>
              {item.title}
            </MerchandiseTitle>

            <MerchandiseCategory>
              {item.category}
            </MerchandiseCategory>

            <MerchandisePrice>
              {formatPrice(item.priceUSD)}
            </MerchandisePrice>

            <MerchandiseDescription>
              {item.description}
            </MerchandiseDescription>

            {item.available && (
              <>
                {item.category === 'Shirt' && (
                  <SizeSelector>
                    <SizeLabel>Select Size:</SizeLabel>
                    <SizeOptions>
                      {item.sizes.map(size => (
                        <SizeButton
                          key={size}
                          selected={selectedSizes[item.id] === size}
                          onClick={() => setSelectedSizes(prev => ({ ...prev, [item.id]: size }))}
                        >
                          {size}
                        </SizeButton>
                      ))}
                    </SizeOptions>
                  </SizeSelector>
                )}

                <AddToCartButton
                  onClick={() => addToCart(item)}
                  disabled={item.category === 'Shirt' && !selectedSizes[item.id]}
                >
                  <FaShoppingCart />
                  Add to Cart
                </AddToCartButton>

                <div style={{ textAlign: 'center', marginTop: '0.5rem' }}>
                  <small style={{ color: '#888888' }}>
                    Or <a href={item.link} target="_blank" rel="noopener noreferrer" style={{ color: '#ff0000', textDecoration: 'none' }}>buy on Bandcamp</a>
                  </small>
                </div>
              </>
            )}

            {!item.available && (
              <SoldOutBadge>SOLD OUT</SoldOutBadge>
            )}
          </MerchandiseCard>
        ))}
      </MerchandiseGrid>

      {/* Cart Icon */}
      <CartIcon onClick={() => setCartOpen(true)}>
        <FaShoppingCart />
        {getCartItemCount() > 0 && (
          <CartBadge>{getCartItemCount()}</CartBadge>
        )}
      </CartIcon>
      <CartLabel>Cart</CartLabel>

      {/* Cart Modal */}
      {cartOpen && (
        <CartModal
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <CartContent>
            <CartHeader>
              <CartHeaderLeft>
                <CartTitle>Shopping Cart ({getCartItemCount()} items)</CartTitle>
                <CartSubtitle>Your items are on their way!</CartSubtitle>
              </CartHeaderLeft>
              <CartHeaderRight>
                <CurrencySelector>
                  <FaGlobe />
                  <CurrencyDisplay>{userCurrency}</CurrencyDisplay>
                </CurrencySelector>
                <CurrencyDropdown
                  value={userCurrency}
                  onChange={(e) => setUserCurrency(e.target.value)}
                >
                  {/* Add your currency options here */}
                  <option value="USD">USD</option>
                  <option value="EUR">EUR</option>
                  <option value="SEK">SEK</option>
                  <option value="NOK">NOK</option>
                  <option value="DKK">DKK</option>
                  <option value="GBP">GBP</option>
                  <option value="CAD">CAD</option>
                  <option value="AUD">AUD</option>
                  <option value="JPY">JPY</option>
                  <option value="CHF">CHF</option>
                  <option value="PLN">PLN</option>
                  <option value="CZK">CZK</option>
                  <option value="HUF">HUF</option>
                  <option value="RON">RON</option>
                  <option value="BGN">BGN</option>
                  <option value="BRL">BRL</option>
                  <option value="ARS">ARS</option>
                  <option value="CLP">CLP</option>
                  <option value="MXN">MXN</option>
                  <option value="ILS">ILS</option>
                  <option value="AED">AED</option>
                  <option value="TRY">TRY</option>
                  <option value="ZAR">ZAR</option>
                  <option value="EGP">EGP</option>
                  <option value="NGN">NGN</option>
                  <option value="KRW">KRW</option>
                  <option value="SGD">SGD</option>
                  <option value="NZD">NZD</option>
                  <option value="INR">INR</option>
                  <option value="CNY">CNY</option>
                  <option value="RUB">RUB</option>
                  <option value="UAH">UAH</option>
                  <option value="ISK">ISK</option>
                </CurrencyDropdown>
                <CloseButton onClick={() => setCartOpen(false)}>
                  <FaTimes />
                </CloseButton>
              </CartHeaderRight>
            </CartHeader>

            {cart.length === 0 ? (
              <EmptyCart>
                <h3>Your cart is empty</h3>
                <p>Add some merchandise to get started!</p>
              </EmptyCart>
            ) : (
              <>
                <CartSummary>
                  <CartSummaryTitle>Order Summary</CartSummaryTitle>
                  <CartSummaryGrid>
                    <CartItemsSection>
                      <SummarySectionTitle>Items ({getCartItemCount()})</SummarySectionTitle>
                      {cart.map((item, index) => (
                        <CartItem key={`${item.id}-${item.size}-${index}`}>
                          <CartItemImage
                            src={item.image}
                            alt={item.title}
                            onError={(e) => {
                              e.target.style.display = 'none';
                              e.target.nextSibling.style.display = 'flex';
                            }}
                          />
                          <CartItemImageFallback style={{ display: 'none' }}>
                            {item.category === 'Shirt' ? <FaTshirt /> : 
                             item.category === 'Hat' ? <FaHatCowboy /> : 
                             item.category === 'Vinyl' ? <FaMusic /> : 
                             item.category === 'Button/Pin/Patch' ? <FaGuitar /> : 
                             item.category === 'Other Apparel' ? <FaGem /> : <FaCircle />}
                          </CartItemImageFallback>
                          <CartItemDetails>
                            <CartItemTitle>{item.title}</CartItemTitle>
                            {item.category === 'Shirt' && (
                              <CartItemSize>Size: {item.size}</CartItemSize>
                            )}
                            <CartItemPrice>{formatPrice(item.priceUSD)}</CartItemPrice>
                          </CartItemDetails>

                          <QuantityControl>
                            <QuantityButton
                              onClick={() => updateQuantity(item.id, item.size, item.quantity - 1)}
                            >
                              <FaMinus />
                            </QuantityButton>
                            <QuantityDisplay>{item.quantity}</QuantityDisplay>
                            <QuantityButton
                              onClick={() => updateQuantity(item.id, item.size, item.quantity + 1)}
                            >
                              <FaPlus />
                            </QuantityButton>
                          </QuantityControl>

                          <RemoveButton
                            onClick={() => removeFromCart(item.id, item.size)}
                          >
                            <FaTimes />
                          </RemoveButton>
                        </CartItem>
                      ))}
                    </CartItemsSection>

                    <CartSummarySection>
                      <SummarySectionTitle>Order Details</SummarySectionTitle>
                                             <SummaryRow>
                         <SummaryLabel>Subtotal:</SummaryLabel>
                         <SummaryValue>
                           {formatPrice(getCartTotal())} ({getCurrencySymbol(userCurrency)})
                         </SummaryValue>
                       </SummaryRow>
                       <SummaryRow>
                         <SummaryLabel>Total:</SummaryLabel>
                         <SummaryValue>
                           {formatPrice(getCartTotal())} ({getCurrencySymbol(userCurrency)})
                         </SummaryValue>
                       </SummaryRow>
                    </CartSummarySection>
                  </CartSummaryGrid>
                </CartSummary>

                {/* TODO: Replace shipping calculator with Shopify integration */}

                {cart.length > 0 && (
                  <CheckoutSection>
                    <CheckoutTitle>
                      <FaExternalLinkAlt />
                      Secure Checkout
                    </CheckoutTitle>

                    <OrderSummary>
                      <SummaryRow>
                        <SummaryLabel>Subtotal:</SummaryLabel>
                        <SummaryValue>${getCartTotal().toFixed(2)}</SummaryValue>
                      </SummaryRow>
                      <SummaryRow>
                        <SummaryLabel>Total:</SummaryLabel>
                        <SummaryValue>${getCartTotal().toFixed(2)}</SummaryValue>
                      </SummaryRow>
                    </OrderSummary>

                    <div style={{ 
                      textAlign: 'center', 
                      padding: '2rem',
                      background: 'rgba(255, 255, 255, 0.02)',
                      borderRadius: '12px',
                      border: '1px solid rgba(255, 255, 255, 0.1)'
                    }}>
                      <h3 style={{ color: '#ffffff', marginBottom: '1rem' }}>
                        ðŸ›’ Ready to Checkout?
                      </h3>
                      <p style={{ color: '#cccccc', marginBottom: '1.5rem' }}>
                        Shopify integration coming soon! This will provide a seamless checkout experience.
                      </p>
                      <button 
                        style={{
                          background: 'linear-gradient(45deg, #ff0000, #cc0000)',
                          color: '#ffffff',
                          border: 'none',
                          padding: '1rem 2rem',
                          borderRadius: '25px',
                          fontSize: '1.1rem',
                          fontWeight: '600',
                          cursor: 'pointer',
                          transition: 'all 0.3s ease'
                        }}
                        onMouseEnter={(e) => {
                          e.target.style.transform = 'translateY(-2px)';
                          e.target.style.boxShadow = '0 8px 25px rgba(255, 0, 0, 0.3)';
                        }}
                        onMouseLeave={(e) => {
                          e.target.style.transform = 'translateY(0)';
                          e.target.style.boxShadow = 'none';
                        }}
                      >
                        ðŸš§ Checkout Coming Soon
                      </button>
                    </div>

                  </CheckoutSection>
                )}
              </>
            )}
          </CartContent>
        </CartModal>
      )}
    </MerchandiseContainer>
  );
};

export default Merchandise;
